business_objects.config_function:
function ($stateProvider, $urlRouterProvider, $locationProvider, $httpProvider, $controllerProvider) {
    console.log('noonian.dbui config()');
    var appUrlBase = '/dbui/index';

    $urlRouterProvider
      .otherwise(appUrlBase);

    $locationProvider.html5Mode({enabled:true, requireBase:true});

    // $httpProvider.interceptors.push('NoonAuthInterceptor');

    //Allow dynamical adding of controllers...
    //noonianAppModule.registerController = $controllerProvider.register;

    //Function used by DBUI to query for theObject for className/id stateParams
    var resolveTheObject = function($stateParams, db) {
        return db.init().then(function() {

          var className = $stateParams.className;
          var boId = $stateParams.id;
          if(className) {
             var Model = db[className];
              if(boId && Model) {
                return Model.findOne({_id:boId}).$promise;
              }
              else if(Model) {
                  if($stateParams.copyObject) {
                      var templateObj = _.clone($stateParams.copyObject);
                      delete templateObj._id;
                      return new Model(templateObj);
                  }
                  else {
                      return new Model({});
                  }
              }
              else {
                return null;
              }
          }

          return null;
        });

    };


    var resolvePerspectiveObj = function(perspectiveType, $stateParams, Dbui) {
      var className = $stateParams.className;
      var perspectiveName = $stateParams.perspective || 'default';

      if(className) {
        return Dbui.getPerspective(perspectiveName, className, perspectiveType);
      }
      else {
        return null;
      }

    };

    $stateProvider
      .state('dbui', {
        abstract:true,
        views: {
          'navBar' : {
            templateUrl: 'dbui/core/navbar.html'
          },
          'sideBar' : {
            templateUrl: 'dbui/core/sidebar.html'
          },
          'mainContainer' : {
            template: '<ui-view/>' //Child states populate this
          }
        },
        resolve: {
            //IF DBUI SILENTLY FAILS TO LOAD:
            // check these injected providers for errors arising from using identifiers that haven't been declared
            initApi: function(db, NoonAuth, Dbui) {
                console.log('Initializing "dbui" ui-router state...');
                return db.init().then(NoonAuth.init).then(Dbui.init);
            }
        }
      })
      .state('dbui.home', {
        url:appUrlBase,
        templateUrl: 'dbui/core/state/home.html',
        controller:'dbui_HomePageCtrl'
      })
    //   .state('dbui.login', {
    //     url:appUrlBase+'/login',
    //     templateUrl: 'dbui/core/login.html'
    //   })
      .state('dbui.list', {
        url:appUrlBase+'/list/:className/:perspective',
        templateUrl: 'dbui/core/state/list.html',
        controller:'dbui_ListCtrl',
        resolve: {
          listPerspective:  function($stateParams, Dbui, DbuiFieldType) {
            return DbuiFieldType.cacheTypeInfoForClass($stateParams.className, 'view')
                .then(resolvePerspectiveObj.bind(null,'list', $stateParams, Dbui));
          }
        }
      })
      .state('dbui.folders', {
        url:appUrlBase+'/folders/:className/:perspective',
        templateUrl: 'dbui/core/state/folders.html',
        controller:'dbui_FoldersCtrl',
        resolve: {
          folderPerspective:  function($stateParams, Dbui, DbuiFieldType) {
            return DbuiFieldType.cacheTypeInfoForClass($stateParams.className, 'view')
                .then(resolvePerspectiveObj.bind(null,'folder', $stateParams, Dbui));
          }
        }
      })
      .state('dbui.view', {
        url:appUrlBase+'/view/:className/:id/:perspective',
        templateUrl: 'dbui/core/state/view.html',
        controller:'dbui_ViewCtrl',
        resolve: {
          theObject: resolveTheObject,
          viewPerspective:  function($stateParams, Dbui, DbuiFieldType) {
            return DbuiFieldType.cacheTypeInfoForClass($stateParams.className, 'view')
                .then(resolvePerspectiveObj.bind(null,'view', $stateParams, Dbui));
          }
        }
      })
      .state('dbui.edit', {
        url:appUrlBase+'/edit/:className/:id/:perspective',
        templateUrl: 'dbui/core/state/edit.html',
        controller:'dbui_EditCtrl',
        resolve: {
          theObject: resolveTheObject,
          editPerspective:  function($stateParams, Dbui, DbuiFieldType) {
            return DbuiFieldType.cacheTypeInfoForClass($stateParams.className, 'edit')
                .then(resolvePerspectiveObj.bind(null, 'edit', $stateParams, Dbui));
          }
        }
      })
      .state('dbui.create', {
        url:appUrlBase+'/create/:className/:perspective',
        templateUrl: 'dbui/core/state/edit.html',
        controller:'dbui_EditCtrl',
        resolve: {
          theObject: resolveTheObject,
          editPerspective:  function($stateParams, Dbui, DbuiFieldType) {
            return DbuiFieldType.cacheTypeInfoForClass($stateParams.className, 'edit')
                .then(resolvePerspectiveObj.bind(null, 'edit', $stateParams, Dbui));
          }
        },
        params:{
            copyObject:null
        }
      })
      .state('dbui.custompage', {
        url:appUrlBase+'/custompage/:key',
        templateUrl: 'dbui/core/state/custompage.html',
        params:{
          id:null,
          key:null,
          resourcePath:null,
          perspective:null,
          extraParams:null
        }
      })
      ;
}

function ($controllerProvider, $locationProvider) {

    $locationProvider.html5Mode(true);

    $controllerProvider.register('LoginController', function($scope, $http, $location, $window) {
        console.log('LoginController');

        document.cookie = 'access_token=; expires=Thu, 01 Jan 1970 00:00:01 GMT;';

        $scope.message = "Please enter username and password below";
        $scope.postData = {};

        var urlParams = $location.search();


        $scope.submit = function() {

            $http({
                method:'POST',
                url:'auth/login',
                data:$scope.postData
            }).then(
                //Success:
                function(resp) {
                    console.log('Login response: ', resp);
                    var redirectLink = (urlParams && urlParams.originalUrl) || '.';
                    $window.location.href = redirectLink;
                },
                //ERROR
                function(resp) {
                    console.log('ERROR', resp);
                    if(resp.data && resp.data.error === '$invalid_credentials') {
                        $scope.message = 'Invalid login credentials.  Please try again.';
                    }
                    else if(resp.data && resp.data.error === '$missing_phone') {
                        $scope.message = 'Two-factor authentication is required, but your user account is missing a phone number.  Please contact an administrator to resolve the issue.';
                    }
                    else if(resp.data && resp.data.twoFactorRequired) {
                        $scope.twoFactor = true;
                        $scope.message = 'Two-factor authentication required.  Please type in code sent to your phone.';
                    }
                    else {
                        $scope.message = (resp.data && resp.data.error) || resp.statusText
                    }
                }
            );

        }
    })
}

business_objects.function:
function ($stateParams, $scope, DbuiAction, NoonI18n, theObject, viewPerspective) {


    var className = $scope.boClass;

    if(!className) {
        className = $scope.boClass = $stateParams.className;
    }

    $scope.theObject = theObject;
    $scope.viewPerspective = viewPerspective;


    $scope.labels = NoonI18n.getBoLabelGroup(className);


    // function(perspectiveObj, contextBo, actionObj, argsObj)
    $scope.invokeAction = DbuiAction.invokeAction.bind(DbuiAction, viewPerspective, theObject);

    if(viewPerspective.actions) {
        $scope.actionList = DbuiAction.unaliasActionList(viewPerspective.actions);
    }

    if(viewPerspective.recordActions) {
        $scope.recordActionList = DbuiAction.unaliasActionList(viewPerspective.recordActions);
    }

    if($scope.setPageTitle)
        $scope.setPageTitle('View '+theObject._disp);

}

function (DbuiFieldType, db, $timeout) {
    return {
      templateUrl: 'dbui/core/helper/querybuilder_term.html',
      restrict: 'E',
      scope: {
        fieldList: '<' //the augmented field list from Dbui.getAugmentedFieldList
      },
      require: 'ngModel',

      link: function(scope, element, attrs, ngModel) {

        //viewValue: {field:'name_of_field.possiblydotted', op:{op:'$someOp', ...}, value:'value from fieldeditor'}
        //modelValue: a valid mongo query term, e.g.
        //  {'field':{$op:'value'}}  <-- returned from DbuiTypeService

        var retainHashKey = function(src, dest) {
          //WTF: Retain $$hashKey so iteration in QueryBuilder template doesn't throw into an infinite format/parse loop!
          if(src && src.$$hashKey)
            dest.$$hashKey = src.$$hashKey;
        };


        //Turn a {'field':{'$op':'value'}} "queryClause" into a {fieldInfo:..., opInfo:..., value:'value'} "flattened" clause
        var flattenQueryClause = function(queryClause) {

            var fieldList = scope.fieldList;

            var term = {};

            for(var fieldSelector in queryClause) {
                //Scan past any junk:
                if(fieldSelector.indexOf('$$') === 0) { //if the incoming query is on a $scope, it gets a $$hashkey key.
                    continue;
                }

                var dotPos = fieldSelector.indexOf('.');
                var fieldBase = false;
                if(dotPos > -1) {
                    fieldBase = fieldSelector.substring(0, dotPos+1);
                }

                //Scan through fieldList to find matching fieldInfo
                for(var i=0; i < scope.fieldList.length; i++) {
                    var fi = scope.fieldList[i];
                    if(fi.fieldName === fieldSelector) {
                        term.fieldInfo = fi;
                    }
                    else if(fieldBase && fi.refPlaceholder && fi.fieldName === fieldBase) {
                        fi.expand();
                        i--;
                    }
                }

                //In case query is the shorthand equals, 
                // convert {field:"value"} -> {field:{$eq:"value"}}
                var opClause = queryClause[fieldSelector];

                if(!(opClause instanceof Object)) {
                    queryClause[fieldSelector] = opClause = {$eq:opClause};
                }

                var opArray = Object.keys(opClause);

                if(opArray.length > 1)
                    console.log("WARNING: multi-condition clause being linked to a single queryTermBuilder!!!");

                    if(opArray.length > 0) {
                        term.opInfo = DbuiFieldType.getOpInfo(term.fieldInfo.td, opArray[0]);
                        term.value = queryClause[fieldSelector][opArray[0]];
                    }

                    break;
            }

            return term;
        };

        //$formatter: query term -> $viewValue
        ngModel.$formatters.push(function(queryClause) {
          // console.log('TB: formatting this clause to viewValue:', queryClause);


          var newViewValue = queryClause ? flattenQueryClause(queryClause) : {};
          // console.log('TB: heres what it came up with: ', newViewValue);

          retainHashKey(queryClause, newViewValue);

          return newViewValue;

        });

        //$parser: $viewValue -> query term
        ngModel.$parsers.push(function(viewValue) {
          // console.log('TB: parsing this viewValue into a clause:', viewValue);

          if(!viewValue || !viewValue.fieldInfo || !viewValue.opInfo)
            return null;

          var fieldName = viewValue.fieldInfo.fieldName;
          var op = viewValue.opInfo.op;
          var value = viewValue.value;

          //Unflatten viewValue: {'field':{'$op':'the value'}}
          var queryClause = {};
          queryClause[fieldName] = {};
          queryClause[fieldName][op] = value !== undefined ? value : true;


          retainHashKey(viewValue, queryClause);

          return queryClause;

        });

        //$watch: our scope -> $viewValue
        // ***it's important to watch ALL of scope that affects ngModel in a single expression;
        //  otherwise, you may get competing $setViewValue calls that stomp on one another
        // ***also important: don't assign the scope object directly as the view value!
        scope.$watch('term', function() {
          //When any part of the term changes ('true' param below is deep watch)
          //  replace the viewValue object with a new object
          var term = scope.term;
          var newViewValue = {fieldInfo:term.fieldInfo, opInfo:term.opInfo, value:term.value};
          retainHashKey(term, newViewValue);
          // console.log('TB: scope watch invoked -> setting view value: ', newViewValue);

          ngModel.$setViewValue(newViewValue);
        }, true );

        //$render: $viewValue -> our scope
        ngModel.$render = function() {
          // console.log('TB: rendering this viewValue to scope:', ngModel.$viewValue);
          //scope object can refer directly to the ngModel viewValue (just not the other way around!)

          var term = scope.term = ngModel.$viewValue;
          // console.log("rendering ", term);
          //Make sure UI elements are synced up to this view value:
          scope.fieldChanged(term.fieldInfo, false);
        };

      },

      controller:function($scope) {

        $scope.fieldChanged = function(fieldInfo, resetValue) {
            // console.log('fieldChanged', fieldInfo, resetValue);

          if(fieldInfo.refPlaceholder) {
            return fieldInfo.expand();
          }

          $scope.fieldSelectorOpen = false;

          var term = $scope.term;
          term.fieldInfo = fieldInfo;

          //Update the OpList so that the op selector shows the appropriate ops for this field type:
          if(fieldInfo && fieldInfo.td) {
            var opList = $scope.opList = DbuiFieldType.getOpList(fieldInfo.td);

            //Make sure the term.opInfo is valid...
            var foundIt = false;
            var currOp = term.opInfo ? term.opInfo.op : null;
            for(var i=0; i < opList.length; i++) {
              if(opList[i].op === currOp) {
                foundIt = true;
                term.opInfo = opList[i];
                break;
              }
            }
            if(!foundIt) {
              term.opInfo = opList[0];
            }
            $scope.opChanged(resetValue);
          }

          if(resetValue)
            $scope.term.value = undefined;
        };

        var fundamentalTypeDesc = function(td) {
          return td instanceof Array ? td[0] : td;
        };

        $scope.opChanged = function(resetValue) {
        //   console.log('opChanged invoked', resetValue);
          var term = $scope.term;
          var opInfo = term.opInfo;

          if(opInfo) {

            var currType = $scope.fieldEditorTypeDesc ? $scope.fieldEditorTypeDesc.type : false;


            //If the op requires a specific editor, set the scope's typeDesc so that it is used...
            if(typeof opInfo.editor === 'boolean')
              $scope.fieldEditorTypeDesc = fundamentalTypeDesc(term.fieldInfo.td);
            else //editor is a typedesc object
              $scope.fieldEditorTypeDesc = opInfo.editor;

            // if(currType !== $scope.fieldEditorTypeDesc.type) {
                // $scope.fieldEditorVisible = false;
            // }


            //Nullify term value if type changed
            if(resetValue && ($scope.fieldEditorTypeDesc.type !== currType || !opInfo.editor)) {
              $scope.term.value = undefined;
            }

            //Coax angular into destroying the fieldEditor directive so it can be rebuilt using the new fieldEditorTypeDesc
            $scope.fieldEditorVisible = false;
            $timeout(function() {
                $scope.fieldEditorVisible = $scope.fieldEditorTypeDesc && $scope.term.opInfo && $scope.term.opInfo.editor;
            });
          }
        }



      }
    };
  }

business_objects.doc:
Directive to render a "displayValue" to dom; utilizes {@link TypeDescriptor| type descriptor} to dynamically choose which FieldTypeUiSpec to render with.
@param displayValue value being rendered
@param typeDesc type descriptor for the value being rendered
@param {boolean} abbreviated show an 'abbreviated' view of the value (if applicable - depends upon specific type)
@param fieldCustomizations type-specific {@link FieldCustomizations|field customizations} to apply

business_objects.function:
function ($compile,  DbuiFieldType, $injector) {
    return {
      template: '<div></div>',
      restrict: 'E',

      scope: {
        displayValue: '=',
        typeDesc: '<',
        abbreviated: '<?',
        fieldCustomizations: '<?', //From perspective.field_customizations
        contextObject: '<?'
      },

      link: function (scope, element, attrs, ngModel) {
          var td = scope.typeDesc;
          if(!td) {
              console.error('Missing typeDesc in dbuiFieldEditor directive');
              return;
          }

          DbuiFieldType.getSpec(td, 'view', scope.fieldCustomizations).then(function(specObj){
                            var templateHtml = specObj.template;
              var compiledTemplate = $compile(templateHtml);

              element.append(compiledTemplate(scope));
          });

      },

      controller: function($scope) {
          DbuiFieldType.getSpec($scope.typeDesc, 'view', $scope.fieldCustomizations).then(function(specObj){

              if(specObj && specObj.controller_fn) {
                  var fnString = specObj.controller_fn;
                  try {
                      var toCall;
                      eval("toCall = "+fnString);
                      if(typeof toCall === 'function') {
                          return $injector.invoke(toCall, this, {$scope:$scope});
                      }
                      else {
                          console.error('bad controller function for typeDesc', $scope.typeDesc);
                      }
                  }
                  catch(err) {
                      console.error('bad controller function for typeDesc', $scope.typeDesc, err);
                  }
              }

          });
      }

    };
  }

function () {
    return {
        templateUrl:'dbui/core/helper/date_string_editor.html',

        restrict: 'E',

        require:'ngModel',

        scope: {
            displayFormat: '@',
            elemId:'@'
        },


        link: function(scope, iElement, iAttributes, ngModel) {

            //Add listener to clear text box when it contains invalid date
            var textBox = iElement.find('input');
            textBox.on('blur', function() {
                if(textBox.val() && (!scope.binding || !scope.binding.value)) {
                    textBox.css('background-color','red');
                }
                else {
                    textBox.css('background-color','');
                }
            });

            //1. Wire up converter for ng-model object (string)--> internal $viewValue representation (date)
            ngModel.$formatters.push(function(modelValue) {
                if(modelValue) {
                    return {value:new Date(moment(modelValue).format())};
                }
                return {value:null};
            });

            //2. Wire up converter for internal $viewValue representation (date) --> ng-model object (string)
            ngModel.$parsers.push(function(viewValue) {
                if(viewValue && viewValue.value instanceof Date) {
                    return moment(viewValue.value).format('YYYY-MM-DD');
                }

                return null;
            });

            //3. Wire up trigger for scope object --> $viewValue
            scope.$watch('binding', function() {
                //must *replace* the viewValue object in order for change to propogate to ng-model!
                if(scope.binding && !angular.equals(ngModel.$viewValue, scope.binding)) {
                    ngModel.$setViewValue({value:scope.binding.value});
                    if(scope.binding.value) {
                        textBox.css('background-color','');
                    }
                }

            }, 
            true); 

            //4. Wire up callback for $viewValue update --> scope object
            ngModel.$render = function() {
                if(!scope.binding) {
                    scope.binding = {};
                }
                if(!angular.equals(ngModel.$viewValue, scope.binding)) {
                    scope.binding.value = ngModel.$viewValue.value;
                }
            };


        },


      controller: function($scope) {

        $scope.dateOptions = {
          formatYear: 'yyyy'
        };


        if(!$scope.displayFormat) {
            $scope.displayFormat = 'MMMM dd, yyyy'; 
        }

        $scope.altFormats = [
            'MMM d, yyyy',
            'MMM dd, yyyy',
            'M/d/yy',
            'MM/dd/yy',
            'M/d/yyyy',
            'MM/dd/yyyy',
            'M-d-yy',
            'MM-dd-yy',
            'M-d-yyyy',
            'MM-dd-yyyy',
        ];

        $scope.pickerOpen = false;

        $scope.togglePicker = function() {
            $scope.pickerOpen = !$scope.pickerOpen;
        };


      }

    };
  }

function ($http, $q) {

	return {
		getParameter: function(paramKey) {
			var deferred = $q.defer();

			$http.get('config/param/'+paramKey)
				.success(function(data) {
					var responseObj = angular.fromJson(data);
					if(responseObj.error) {
						deferred.reject(responseObj.error);
					}
					else {
						deferred.resolve(responseObj.result);
					}
				})
				.error(function(err) {
					deferred.reject(err);
				});

			return deferred.promise;
		},

		customizeParameter: function(paramKey, value) {
		    var deferred = $q.defer();

			$http.post('config/customizeParam/'+paramKey, {value:value})
				.success(function(data) {
				    // console.log('customizeParam result', data);
					var responseObj = angular.fromJson(data);
					if(responseObj.error) {
						deferred.reject(responseObj.error);
					}
					else {
						deferred.resolve(responseObj.result);
					}
				})
				.error(function(err) {
					deferred.reject(err);
				});

			return deferred.promise;

		}

	};

}

function (uibDateParser, $filter) {

    return {
        templateUrl:'dbui/core/helper/time_string_editor.html',
        restrict: 'E',

        scope: {
            elemId: '@',
            displayFormat: '@'
        },

        require: 'ngModel', 

        link: function(scope, iElement, iAttributes, ngModel) {

            var displayFormat = scope.displayFormat;

            var dateFormatter = $filter('date');

            var stringToDate = function(stringDate) {
                //14:48:00.000
                var parsed = /(\d{2}):(\d{2}):(\d{2}).(\d{3})/.exec(stringDate);
                var h = +parsed[1];
                var m = +parsed[2];

                //seconds/millis optional
                var s = parsed.length >= 4 ? +parsed[3] : 0;
                var ms = parsed.length >= 5 ? +parsed[4] : 0;

                //construct in **local time zone**
                return new Date(1900, 0, 1, h, m, s, ms);

            };

            //1. Wire up converter for ng-model object (ISO string segment)--> internal $viewValue representation (formatted string)
            ngModel.$formatters.push(function(modelValue) {
                if(modelValue) {
                    //modelValue is the time portion of an ISO String (the stuff after T, not including timezone)
                    var d =  stringToDate(modelValue);
                    return {value: dateFormatter(d, displayFormat)};  //format in **local time zone**
                }
                return {value:null};
            });

            //2. Wire up converter for internal $viewValue representation (formatted string) --> ng-model object (ISO string segment)
            ngModel.$parsers.push(function(viewValue) {
                if(viewValue && viewValue.value) {
                    try {
                        var dateObj = uibDateParser.parse(viewValue.value, displayFormat); //parsed to **local time zone**
                        if(dateObj instanceof Date && !isNaN(dateObj.getTime())) {
                            var h = dateObj.getHours();
                            var m = dateObj.getMinutes();
                            var s = dateObj.getSeconds();
                            var ms = dateObj.getMilliseconds();

                            h = (h < 10) ? '0'+h : ''+h;
                            m = (m < 10) ? '0'+m : ''+m;
                            s = (s < 10) ? '0'+s : ''+s;
                            ms = (ms < 10) ? '00'+ms : ( (ms < 100) ? '0'+ms : ''+ms);
                            return h+':'+m+':'+s+'.'+ms;
                        }
                    } catch(e) {}
                }

                return null;
            });

            //3. Wire up trigger for scope object --> $viewValue
            scope.$watch('binding', function() {
                //must *replace* the viewValue object in order for change to propogate to ng-model!
                if(scope.binding &&  !angular.equals(ngModel.$viewValue, scope.binding)) {
                    ngModel.$setViewValue({value:scope.binding.value});
                }
            }, 
            true); 

            //4. Wire up callback for $viewValue update --> scope object
            ngModel.$render = function() {
                if(!scope.binding) {
                    scope.binding = {};
                }
                if(!angular.equals(ngModel.$viewValue, scope.binding)) {
                    scope.binding.value = ngModel.$viewValue.value;
                }

            };

            //For a cleaner interface, hightlight something helpful on focus:
            scope.onFocus = function($event) {
                var domElem = $event.target;
                if(domElem.value) {
                    var rMatch = /(:\d+)[: ]/.exec(domElem.value);

                    if(rMatch) {
                        var selStart = rMatch.index+1; //just past first colon
                        var selEnd = rMatch.index + rMatch[1].length;
                        domElem.setSelectionRange(selStart, selEnd);   
                    }
                }
            };
        },

        controller: function($scope) {

            var displayFormat = $scope.displayFormat;
            if(!displayFormat) {
                displayFormat = $scope.displayFormat = 'h:mm a';
            }

            var dateFormatter = $filter('date');
            var iterDate = new Date(1900, 0, 1, 0, 0, 0, 0);

            $scope.times = [];
            for(var i=0; i < 24; i++) {
                $scope.times.push(dateFormatter(iterDate, displayFormat));
                iterDate.setHours(iterDate.getHours()+1);
            }



            $scope.selectTime = function(time) {
                if(!$scope.binding) {
                    $scope.binding = {};
                }
                $scope.binding.value = time;
            }
        }
    };
}

function () {

      var URL = window.URL || window.webkitURL;

      var getResizeArea = function () {
          var resizeAreaId = 'fileupload-resize-area';

          var resizeArea = document.getElementById(resizeAreaId);

          if (!resizeArea) {
              resizeArea = document.createElement('canvas');
              resizeArea.id = resizeAreaId;
              resizeArea.style.visibility = 'hidden';
              document.body.appendChild(resizeArea);
          }

          return resizeArea;
      };

      var resizeImage = function (origImage) {
          var maxHeight = 300;
          var maxWidth = 250;

          var canvas = getResizeArea();

          var height = origImage.height;
          var width = origImage.width;

          // calculate the width and height, constraining the proportions
          if (width > height) {
              if (width > maxWidth) {
                  height = Math.round(height *= maxWidth / width);
                  width = maxWidth;
              }
          } else {
              if (height > maxHeight) {
                  width = Math.round(width *= maxHeight / height);
                  height = maxHeight;
              }
          }

          canvas.width = width;
          canvas.height = height;

          //draw image on canvas
          var ctx = canvas.getContext('2d');
          ctx.drawImage(origImage, 0, 0, width, height);

          return canvas.toDataURL('image/jpg', 0.7);
      };

      return {
          restrict: 'A',
          scope: {
              imageData: '=',
              resizeMaxHeight: '@?',
              resizeMaxWidth: '@?',
              resizeQuality: '@?',
              resizeType: '@?',
          },
          link: function postLink(scope, element, attrs, ctrl) {

              element.bind('change', function (evt) {
                //'change' event triggered when a file is selected.

                  var files = evt.target.files;
                  if(files.length > 0) {
                    var theFile = scope.file = files[0];

                    var imgObj = {
                      name: theFile.name,
                      type: theFile.type
                    };

                    //Transform it to data url; put it into our imgObj
                    var reader = new FileReader();
                    reader.onload = function (e) {
                        imgObj.data = e.target.result;
                    };
                    reader.readAsDataURL(theFile);

                    //Create the thumbnail
                    var image = new Image();

                    image.onload = function() {
                        var dataURL = resizeImage(image);
                        imgObj.thumbData = dataURL;
                        scope.$apply(function(){
                          scope.imageData = imgObj;
                        });
                    };
                    image.src = URL.createObjectURL(theFile); //Special way of addressing in-memory objects
                  }
              });
          }
      };
  }

business_objects.doc:
Renders a full-featured list of 1st class BusinessObjects, complete w/ pagination and optional search/query.
@param {string} boClass - class name of BusinessObject's to display
@param {Perspective} perspective - "list" perspective object driving the display
@param {boolean) enableSearch - if true, a textbox is displayed to allow full text search against displayed data
@param {boolean} enableQuery - if true, a dbui-query-builder is accessible to provide advanced querying against displayed data (forces enableSearch=true)

business_objects.function:
function ($timeout, $q, db, NoonI18n, DbuiAction, Config,  Dbui, DbuiPerspectiveEditor, $stateParams, DbuiFieldType, DbuiAlert) {
  return {
    templateUrl: 'dbui/reusable/core/object_browser.html',
    restrict: 'E',
    scope: {
      boClass: '<',
      perspective: '<',
      enableSearch: '<?',
      enableQuery: '<?'
    },


    controller:function($scope, NoonWebService) {

        var specialTableActions = {
            edit_perspective:{
                label:'Edit Perspective',
                icon:'fa-list-alt',
                fn: function(args) {
                    return DbuiPerspectiveEditor.showEditorDialog($scope.boClass, $scope.perspective, 'list').then($scope.loadData);
                }
            },
            refresh_data:{
                label:'Refresh Data',
                icon:'fa-refresh',
                fn: function(args) {
                    $scope.loadData();
                }
            }
        };

        /**
         * @private
         * Create a "stub" query for typeDescMap
         */
        var createStubQuery = function() {
            var typeDescMap = $scope.typeDescMap;
            var fieldName = $scope.perspective.fields[0];
            var td = $scope.typeDescMap[fieldName];


            var stub = {};

            var opList = DbuiFieldType.getOpList(td);
            if(opList && opList.length) {
                stub[fieldName] = {};
                stub[fieldName][opList[0].op] = '';
                return stub;
            }
            else {
                //The passed-in field isn't searchable; so we'll pick another:
                for(var field in typeDescMap) {
                    td = typeDescMap[field];
                    opList = DbuiFieldType.getOpList(td);
                    if(opList && opList.length) {
                        stub[field] = {};
                        stub[field][opList[0].op] = '';
                        return stub;   
                    }
                }
            }
        };

        var className = $scope.boClass;
        var perspective = $scope.perspective;

        var BoModel = db[className];
        $scope.objectMetaData = BoModel._bo_meta_data;
        $scope.typeDescMap = BoModel._bo_meta_data.type_desc_map;
        $scope.labels = NoonI18n.getBoLabelGroup(className);


        $scope.dataArray = [];


        if(!perspective.sort) {
            perspective.sort = {};
        }

        if(!perspective.pageState) {

            perspective.pageState = {
                current:1,
                pageSize:10,
                totalRecords:0
            };

            Config.getParameter('sys.dbui.defaultPageSize').then(function(defaultPageSize) {
                perspective.pageState.pageSize = defaultPageSize;
            });

        }

        $scope.pageState = perspective.pageState;


        var searchPromise = null;
        var loadData = $scope.loadData = function() {

            if(searchPromise) {
                $timeout.cancel(searchPromise);
                searchPromise = null;
            }

            var pageSize = perspective.pageState.pageSize;
            var currPage = perspective.pageState.current;  //Bound to paginator

            var sort = perspective.sort;

            var queryDef = perspective.getEffectiveQuery();
            var selectObj = {}; //aka projection

            //Ask only for the fields we're showing
            for(var i=0; i <  perspective.fields.length; i++) {
                var f = perspective.fields[i];
                selectObj[f] = 1;
            }

            //Query options: limit, skip, sort, group-by
            var queryOpts = {limit:pageSize};
            if(currPage > 1) {
                queryOpts.skip = (currPage-1)*pageSize;
            }

            if(sort) {
                queryOpts.sort = sort;
            }

            if(perspective.groupBy) {
                queryOpts.groupBy = perspective.groupBy;
            }

            var stringifyPromise = queryDef ? Dbui.stringifyQueryClause(queryDef, className) : $q.resolve('');

            //Do the query!
            $scope.dataLoading = true;
            var resultList = BoModel.find(queryDef, selectObj, queryOpts);

            $scope.dataArray = resultList;
            $q.all([resultList.$promise, $scope.labels.$promise, stringifyPromise])
            .then( function(resultArr) {
                $scope.dataLoading = false;

                var result = resultArr[0];
                if(perspective.groupBy) {
                    var groupByField = perspective.groupBy;
                    var dataArray = [];
                    for(var i =0; i < result.length; i++) {
                        result[i].__group_header = true;
                        result[i][groupByField] = result[i].group[0][groupByField]; //copy the full value from the first member of the group
                        dataArray.push(result[i]);

                        for(var j=0; j < result[i].group.length; j++) {
                            dataArray.push(result[i].group[j]);
                        }
                    }

                    $scope.dataArray = dataArray;
                }

                $scope.filterDescription = resultArr[2];

                var pageState = perspective.pageState;
                pageState.totalRecords = result.nMatched; //result._meta.nMatched;
                pageState.totalPages = Math.ceil(pageState.totalRecords/pageState.pageSize);
                pageState.rangeStart = pageState.pageSize * (pageState.current - 1) + 1;

                var rangeEnd = pageState.rangeStart + pageState.pageSize - 1;
                if(rangeEnd > pageState.totalRecords) {
                    pageState.rangeEnd = pageState.totalRecords;
                }
                else {
                    pageState.rangeEnd = rangeEnd;
                }
            },
            function(err) {
                $scope.dataLoading=false;
                console.error('DATA RETRIEVE ERROR:', err);
                DbuiAlert.danger('Error retrieving data (see log) ');
            });
        };

        $scope.pageChanged = loadData; //Called by paginator event

        $scope.searchStringChanged = function() {
            if(searchPromise) {
                $timeout.cancel(searchPromise);
            }

            //execute search half a second after search string stops changing
            var ss = perspective.searchString;

            if(ss != null && (ss.length >= 3 || ss === '')) {
                searchPromise = $timeout(loadData, 500);
            } 
        };

        $scope.advancedSearch = function(enable) {
            perspective.isAdvancedSearch = enable;

            //If we switch to advanced search, and we don't have a searchQuery already on the scope,
            // initialize with a stub query
            if(enable) {
                if(!perspective.filter) {
                    perspective.filter = createStubQuery();
                }

                perspective.searchString = '';
            }

            if(!enable) {
                perspective.filter = null;
                loadData();
            }
        };

        $scope.execSearch = function() {
            loadData();
        };

        //The config object for the dbui-object-table:
        $scope.objectTableConfig = {
            cellEdit: false,
            onSort: loadData,
            aliasActions:specialTableActions
        };

        //Do the initial data load:
        loadData();

        $scope.saveQuery = function() {
            //Get a name for the query
            //is it for me or for everyone?
            //append it to perspective.savedQueries
            //save the perspective
            Dbui.showDialog(
                'Save Query', 
                'Please provide a description for the saved query.', 
                {name:{type:'string'}}, 
                {layout:['name']}
            ).then(function(result) {

                var wsParams = {
                    title:result.name,
                    theQuery:perspective.filter,
                    perspective:perspective.name,
                    class_name:className
                };

                NoonWebService.call('dbui/saveQuery', wsParams).then(function(result) {
                    // console.log(result);
                    perspective.savedQueries = result.queryList;
                });
            });
        };

        $scope.loadQuery = function(q) {
            perspective.filter = _.cloneDeep(q);
            loadData();
        };

    } // end controller definition
  }
}

function ($http, $q, $injector, BusinessObjectModelFactory, TypeDescMap,BusinessObjectLabelGroup) {

  var initPromise = false; //Fulfilled when this factory is fully initialized
  var modelCache = {};  //Maps both BusinessObjectDef id AND classname to Model object
  var modelArr = [];


  /**
   * Convience to check/handle errors returned by webservice.
   * @private
   **/
  var handleWsCallError = function(method, md, response) {
    //An http error code was returned...
    console.log('ERROR response in ws call '+method, md, response);
    if(response.data.error)
      this.reject(response.data.error);
    else
      this.reject(response);
  };


  //modelStaticFunctions collection-level data access: find, count, batch update & remove.
  //  *these functions must be called w/ a this = _bo_meta_data
  var modelStaticFunctions = {

    /**
     * @function db.BusinessObjectModel.find
     * find a set of BusinessObjects
     *
     * @param {Object} conditions - query conditions (MongoDb style)
     * @param {?Object} projection - to include/exclude specific fields (MongoDb style)
     * @param {?Object} options - sort, limit, skip, group-by
     * @return {Array} A placeholder array that will be populated by the result objects on completion of call.
     *                 Contains $promise property that is fulfilled once webservice call is completed.
     */
    find:function(conditions, projection, options) {
      var deferred = $q.defer();

      var returnArr = [];
      returnArr.$promise = deferred.promise;

      var boMetaData = this._bo_meta_data;
      // console.log('Executing find: ', boMetaData, conditions, projection, options);

      var wsParams = {
        where:conditions,
        select:projection
      };
      _.assign(wsParams, options);

      $http({
        method:'GET',
        url:'db/'+boMetaData.class_name,
        params:wsParams
      }).then(

        function(response) {
          var responseData = response.data;

          if(responseData.error) {
            deferred.reject(responseData.error);
            return;
          }

          returnArr.nMatched = responseData.nMatched;
          returnArr._bo_meta_data = boMetaData;

          var MyModel = modelCache[boMetaData.class_name];

          _.forEach(responseData.result || [], function(resultObj) {
            if(!resultObj.group) {
                if(resultObj.__t) {
                    returnArr.push(new modelCache[resultObj.__t](resultObj));
                }
                else {
                    returnArr.push(new MyModel(resultObj));
                }
            }
            else {
              //Handle group-by
              var groupObj = {
                _id: resultObj._id,
                count: resultObj.count,
                group:[]
              };
              returnArr.push(groupObj);
              _.forEach(resultObj.group, function(obj) {
                groupObj.group.push(new MyModel(obj));
              });
            }
          });

          deferred.resolve(returnArr);
        },
        handleWsCallError.bind(deferred, 'db.find()', boMetaData)
      );

      return returnArr;
    },

    /**
     * @function db.BusinessObjectModel.findOne
     * find a single BusinessObject
     *
     * @param {Object} conditions - query conditions (MongoDb style)
     * @param {?Object} projection - to include/exclude specific fields (MongoDb style)
     * @param {?Object} options - sort, limit, skip, group-by
     * @return {BusinessObjectModel} A placeholder BusinessObjectModel instance that will be populated by the result object on completion of call.
     *                 Contains $promise property that is fulfilled once webservice call is completed.
     */
    findOne: function(conditions, projection, options) {
      // console.log('findOne', this, conditions);
      var deferred = $q.defer();

      var boMetaData = this._bo_meta_data;
      var MyModel = modelCache[boMetaData.class_name];

      var returnObj = new MyModel();
      returnObj.$promise = deferred.promise;

      var wsParams = {
        where:conditions
      };

      if(projection)
        wsParams.select = projection;
      if(options)
        _.assign(wsParams, options);

      wsParams.limit = 1;

      // console.log('Executing findOne: ', boMetaData, wsParams);
      $http({
        method:'GET',
        url:'db/'+boMetaData.class_name,
        params:wsParams
      }).then(

        function(response) {
          var responseData = response.data;

          if(responseData.error) {
            deferred.reject(responseData.error);
            return;
          }

          if(responseData.result && responseData.result.length > 0) {
            // _.assign(returnObj, responseData.result[0]);
            returnObj.initialize(responseData.result[0]);
            deferred.resolve(returnObj);
          }
          else {
            deferred.resolve(null);
          }
        },

        handleWsCallError.bind(deferred, 'db.findOne()', boMetaData)
      );

      return returnObj;
    },

    /**
     * @function db.BusinessObjectModel.count
     * count a set of BusinessObjects
     *
     * @param {Object} conditions - query conditions (MongoDb style)
     * @return {promise} fulfilled once webservice call is completed; resolves to a number representing count of matching objects.
     */
    count: function(conditions) {

    }

  };

  //modelInstanceFunctions - object-level db access: save, remove
  // called w/ this containing properties
  var modelInstanceFunctions = {
    /**
     * @function db.BusinessObjectModel#save
     * Create or update 'this' BO
     * @return {promise} fulfilled once webservice call is completed; resolves to 'this'
     */
    save:function() {
      var deferred = $q.defer();
      var boMetaData = this._bo_meta_data;
      var theObject = this;
      var copyObj = {};

      for(var f in boMetaData.type_desc_map) {
        copyObj[f] = this[f];
      }
      if(this._id) {
        copyObj._id = this._id;
      }


      $http({
        method:'POST',
        url:'db/'+boMetaData.class_name,
        data:copyObj
      }).then(
        function(response) {
          var responseData = response.data;

          if(responseData.error || !responseData.result) {
              deferred.reject(responseData.error || 'Object update failed; no result returned');
              return;
          }

          _.assign(theObject, responseData.result);

          deferred.resolve(theObject);
        },
        handleWsCallError.bind(deferred, 'db.save()', boMetaData)
      );
      return deferred.promise;
    },

    /**
     * @function db.BusinessObjectModel#remove
     * delete 'this' BO
     * @return {promise} fulfilled once webservice call is completed; resolves to ws response result
     */
    remove:function() {
      var deferred = $q.defer();
      var boMetaData = this._bo_meta_data;
      var theObject = this;

      if(!theObject._id) {
        deferred.reject('Unable to delete BusinessObject; missing _id');
        return deferred.promise;
      }

      $http({
        method:'DELETE',
        url:'db/'+boMetaData.class_name+'/'+theObject._id
      }).then(
        function(response) {
          var responseData = response.data;

          if(responseData.error || !responseData.result) {
            deferred.reject(responseData.error || 'Object update failed; no result returned');
            return;
          }

          deferred.resolve(responseData.result);
        },
        handleWsCallError.bind(deferred, 'db.remove()', boMetaData)
      );
      return deferred.promise;
    }
  };



  /**
   * Create the DB Model object for a particular business object definition:
   * the DB Model is akin to a mongoose model, used to query a particular class of BusinessObject's, and to update/delete objects
   * @private
   **/
  var createAndCacheModel = function(bod) {

      var instanceMemberFunctions = {};
      _.assign(instanceMemberFunctions, modelInstanceFunctions);

      var staticMemberFunctions = {};
      _.assign(staticMemberFunctions, modelStaticFunctions);

      if(bod._member_functions) {
          _.forEach(bod._member_functions, function(mf) {

              var fnString = mf.function;
              try {
                  var parsedFn;
                  eval("parsedFn = "+fnString);
                  if(typeof parsedFn !== 'function') {
                      throw new Error('eval resulted in non-function');
                  }

                  var toAssign;

                  if(mf.use_injection) {
                      toAssign = $injector.invoke(parsedFn);
                  }
                  else { 
                      toAssign = parsedFn;
                  }

                  if(mf.is_static) {
                      staticMemberFunctions[mf.name] = toAssign;
                  }
                  else {
                      instanceMemberFunctions[mf.name] = toAssign;   
                  }   
              }
              catch(err) {
                  console.error("function parse failed", err);
              }
          });
      }

      var superModel = null;

      if(bod.superclass) {
          superModel = modelCache[bod.superclass._id];
      }

      var NewModel = BusinessObjectModelFactory.getConstructor(
          bod.definition, 
          bod.class_name, 
          bod._field_labels, 
          bod._id, 
          superModel, 
          instanceMemberFunctions, 
          staticMemberFunctions
      );

      modelCache[bod._id] = modelCache[bod.class_name] = NewModel;
      modelArr.push(NewModel);
  };


  modelCache.getModelArr = function() {
      return modelArr;
  }

  /**
   * @function db.init
   *  Initialize db layer on start-up; calls web service to obtain system metadata, populates the api model objects
   * @return {promise} fulfilled on completion
   */
  modelCache.init = function() {

    if(!initPromise) {
      console.log('initializing db factory');

      initPromise =
        $http({
          method:'GET',
          url:'ws/dbui/getSysMetadata'
        }).then(

          function(response) {
            var responseData = response.data || {};

            if(responseData.error) {
              throw responseData.error;
            }

            var bodArray = responseData.result;

            //Get superclasses first on the list
            bodArray.sort(function(x,y) {
              if(!y.abstract === !x.abstract) return 0;
              else if(y.abstract && !x.abstract) return 1;
              else return -1;
            });

            _.forEach(bodArray, function(bod) {
              createAndCacheModel(bod);
            });

            TypeDescMap._init(modelCache);
            BusinessObjectLabelGroup._init(modelCache);

          }
        );


    }
    return initPromise;
  };

  //Return the API for the db layer:
  return modelCache;

}

function (TypeDescMap,BusinessObjectLabelGroup) {

    var BusinessObjectModelFactory = {};


    /**
     * Factory for building a business object model
     * @param definition
     * @param name 
     * @param fieldLabels
     * @param id
     * @param superModel
     * @param instanceFunctions
     * @param modelFunctions
     */
    BusinessObjectModelFactory.getConstructor = function(definition, name, fieldLabels, id, superModel, instanceFunctions, modelFunctions) {


        /**
         * BusinessObjectModel constructor
         * This is what will ultimately accessed via the db.XyzBusinessObject API.
         * @constructor
         */
        var BusinessObjectModel = function(initObj) {
            if(!this || !(this instanceof BusinessObjectModel)) {
                return new BusinessObjectModel(initObj);
            }
            this.initialize(initObj);
        };

        //Separate out initialize function to allow constructing a stub that is 
        // populated asynchronously on response from web service
        Object.defineProperty(BusinessObjectModel.prototype, 'initialize', {
            enumerable:false,writable:false,
            value:function(initObj) {
                var THIS = this;
                if(initObj) {
                  this._id = initObj._id;
                  this.__ver = initObj.__ver;
                  this.__t = initObj.__t;

                  _.forEach(this._bo_meta_data.type_desc_map, function(td, fieldName) {
                    if(td.construct) {
                      THIS[fieldName] = td.construct(initObj[fieldName]);
                    }
                    else if(initObj.hasOwnProperty(fieldName)) {
                      THIS[fieldName] = initObj[fieldName];
                    }
                  });
                }
            }
        });



        //Create the _bo_meta_data object
        var boMetaData = {
            type_desc_map: new TypeDescMap(definition)
        };

        id && (boMetaData.bod_id = id);
        name && (boMetaData.class_name = name);
        fieldLabels && (boMetaData.field_labels = new BusinessObjectLabelGroup(boMetaData.type_desc_map, fieldLabels));

        //Merge in superclass type descriptors
        if(superModel) {
            _.merge(boMetaData.type_desc_map, superModel._bo_meta_data.type_desc_map);
        }


        var propertyConfig = { enumerable:false, writable:false, value:boMetaData };
        Object.defineProperty(BusinessObjectModel, '_bo_meta_data', propertyConfig);
        Object.defineProperty(BusinessObjectModel.prototype, '_bo_meta_data', propertyConfig);


        //Assign the "static" member functions to the constructor
        if(modelFunctions) {
            _.forEach(modelFunctions, function(fn, propertyName) {
                Object.defineProperty(BusinessObjectModel, propertyName, {
                    enumerable:false, writable:false,
                    value: fn
                });
            });
        }

        //Assign "instance" member functions to the prototype
        if(instanceFunctions) {
            _.forEach(instanceFunctions, function(fn, propertyName) {
                Object.defineProperty(BusinessObjectModel.prototype, propertyName, {
                    enumerable:false, writable:false,
                    value: fn
                });
            });
        }


        //Wire up _disp getter
        if(definition._disp) {
            try {
                BusinessObjectModel.prototype._disp_template = _.template(definition._disp);
            }
            catch(err) {
                console.error('ERROR COMPILING _disp TEMPLATE for '+name, definition._disp, err);
            }
        }

        Object.defineProperty(BusinessObjectModel.prototype, '_disp', {
            get: function() {
                var td = this._bo_meta_data.type_desc_map || {};

                if(this._disp_template) {
                    try {
                        return this._disp_template(this);
                    }
                    catch(err) {}
                }
                else if(td.name) {
                    if(this.name) return ''+this.name;
                }
                else if(td.key) {
                    if(this.key) return ''+this.key;
                }
                else if(td.title) {
                    if(this.title) return ''+this.title;
                }

                return this._bo_meta_data.class_name+'['+this._id+']';
            }
        });


        //Process composite field types as special subtypes.  To set a composite field value, you must instantiate a special "sub-model"
        _.forEach(boMetaData.type_desc_map, function(td, fieldName) {
            if(td.type === 'composite' || (td instanceof Array && td[0].type === 'composite') ) {

                var myTd = td instanceof Array ? td[0] : td;

                //Define "sub-model" constructor to allow for creation of objects that can be assigned to this field
                BusinessObjectModel[fieldName] = BusinessObjectModelFactory.getConstructor(
                        myTd.type_desc_map,
                        name+'#'+fieldName, 
                        fieldLabels ? fieldLabels['#'+fieldName] : null
                );

                //TODO: deprecate these construct() functions somehow... 
                //Make constructor accessible via the type descriptor
                Object.defineProperty(myTd, 'construct', {
                    enumerable:false, writable:false, value:BusinessObjectModel[fieldName]
                });


                if(td instanceof Array) {
                    Object.defineProperty(td, 'construct', {
                        enumerable:false, writable:false,
                        value:function(initArr) {
                            var thisTd = this[0];
                            var ret = [];
                            _.forEach(initArr, function(initObj) {
                                ret.push(thisTd.construct(initObj));
                            });

                            return ret;
                        }
                    });
                }
            }
        });//end type_desc_map iteration

        Object.freeze(BusinessObjectModel);


        return BusinessObjectModel;
    }; //end getConstructor definition




    return BusinessObjectModelFactory;
}

function (NoonAction, DbuiAlert, $q, $stateParams, $rootScope, $uibModal, $state, db) {

    var derivedService = Object.create(NoonAction);

    var dbuiCoreActions = {
      'new':{
        label:'New',
        state:'dbui.create',
        icon:'fa-plus-square'
      },
      'duplicate':{
        label:'Duplicate',
        icon:'fa-copy',
        fn: function(args) {
            $state.go('dbui.create', {className:args.className, perspective:args.perspective, copyObject:args.targetObj});
        }
      },
      'list':{
        label:'List',
        state:'dbui.list',
        icon:'fa-list'
      },
      'folder_list':{
        label:'List',
        state:'dbui.folders',
        icon:'fa-folder-o'
      },
      'view':{
        label:'View',
        state:'dbui.view',
        icon:'fa-eye'
      },
      'edit':{
        label:'Edit',
        state:'dbui.edit',
        icon:'fa-pencil-square-o'
      },
      'delete':{
        label:'Delete',
        icon:'fa-times-circle',
        fn: function(args) {
          if(window.confirm('Are you sure you wish to delete this record?')) {
            return args.targetObj.remove().then(function() {
              $state.go('dbui.list', {className:args.className, perspective:args.perspective});
            },
            function(err) {
              alert('error deleting: '+err);
            });
          }

          return null;
        }
      },
      'dialog-view': {
        label:'View',
        icon:'fa-eye',
        fn: function(args) {

          var modalInstance;
          var scope = $rootScope.$new(true);

          var className = scope.boClass = args.className;
          var perspectiveName = scope.perspectiveName = args.perspective;
          var boId = scope.boId = args.targetObj._id || args.targetObj;

          modalInstance = $uibModal.open({
            templateUrl:'dbui/core/view-dialog.html',
            controller:'dbui_ViewCtrl',
            size:'lg',
            scope: scope,
            resolve: {
              theObject: function() {
                return db[className].findOne({_id:boId}).$promise;
              },
              viewPerspective:  function(Dbui) {
                return Dbui.getPerspective(perspectiveName, className, 'dialog-view');
              }
            }
          });
        }
      }

    };

    /**
     * DbuiAction.init
     */
    derivedService.init = function() {
        NoonAction.registerAliases(dbuiCoreActions);

    };



    /**
     * DbuiAction.invokeAction
     * Invokes actionObj, adding in parameters from $stateParams and contextBo.
     * How contextBo is added to params is dependent on the type of action:
     *   state and ws actions -> "id" is populated w/ contextBo._id 
     *   fn and UiAction actions -> "targetObj" is populated w/ contextBo
     */
    derivedService.invokeAction = function(perspectiveObj, contextBo, actionObj, argsObj) {

        // console.log('DbuiAction.invokeAction', perspectiveObj, actionObj, contextBo);
        if(!actionObj) {
            throw new Error('null actionObj not allowed');
        }

      //Append the standard DBUI params to the action object
      var params = actionObj.params ? _.clone(actionObj.params) : {};

      //Always pass className and perspective:
      if(!params.className) {
          params.className = $stateParams.className;
      }
      if(!params.perspective) {
          if(perspectiveObj) {
              params.perspective = perspectiveObj.name;
          }
          else {
              params.perspective = $stateParams.perspective || 'default';  
          }
      }


      //depending on type of action, we add either targetId or targetObj
      if(actionObj.state || actionObj.ws) {
          if(contextBo && contextBo._id)
            params.id = contextBo._id;
          else
            params.id = contextBo;
      }
      else if(actionObj.fn || actionObj.ui_action) {
          params.targetObj = contextBo;
          params.perspectiveObj = perspectiveObj;
      }


      //Anything in argsObj supercedes the actionObj.params
      if(argsObj) {
          _.assign(params, argsObj);
      }

      return NoonAction.invoke(actionObj, params).then(
          function(result) {
            if(result) {
              if(result.error) {
                // alert(result.error);
                DbuiAlert.danger(result.error);
              }

              if(result.message) {
                // alert(result.message);
                DbuiAlert.success(result.message);
              }

              if(result.warning) {
                  DbuiAlert.warning(result.warning);
              }

              if(result.action) {
                  console.log('Chaining action: ', result.action);
                  return NoonAction.invoke(result.action);
              }
            }

            return result;
          },
          function(err) {
              var msg = 'Error invoking action';
              console.log('DbuiAction: '+msg, actionObj, err);
              DbuiAlert.danger(msg+' '+err);
          }

        );
    };

    return derivedService;
}

function ($scope, NoonAction) {

    $scope.invokeAction = function(actionObj) {
      NoonAction.invoke(actionObj);
    };
}

business_objects.doc:
Renders a table of "BusinessObject-like" objects
@param {BusinessObjectLike[]} ngModel - data array that table is bound to
@param {Perspective} perspective - perspective object driving layout of table and available actions
@param {Object} objectMetaData - _bo_meta_data describing the objects being displayed
@param {Object} [config] - extra configuration for driving display and action invocation 
@param {Object} config.aliasActions - map of action aliases to action objects 
@param {Object} config.recordActions - set of actions to make available for each displayed object; these actions are displayed after perspective's recordActions
@param {Object} config.tableActions - set of actions to make available in the gear menu; these actions are displayed after perspective's tableActions
@param {boolean|Object} config.cellEdit - if boolean, allow/disallow global cell edit of field values in table.  if Object, maps field name to boolean value (or function)
@param {Function} [config.onSort] - function called when sorting changes via column header click, after perspective.sort is updated to reflect sort status. if null, sorting is disabled.

business_objects.function:
function (DbuiAction, NoonI18n) {
  return {
    templateUrl: 'dbui/reusable/core/object_table.html',
    restrict: 'E',
    scope: {
      perspective: '<',
      objectMetaData: '<',
      config: '<?'
    },
    require: 'ngModel',


    link: function(scope, element, attrs, ngModelCtrl) {

      //Sync up an objectArray on the scope with ng-model binding.

      //$formatter: formats ng-model value -> $viewValue
      ngModelCtrl.$formatters.push(function(modelValue){
        // console.log('ObjectTable: formatting viewValue',modelValue);
        //We can transform the ng-model value to something we use internally
        return {vv:modelValue};
      });

      //$parser: parses $viewValue -> ng-model value
      ngModelCtrl.$parsers.push(function(viewValue){
        // console.log('ObjectTable: parsing viewValue', viewValue);
        //Transform internal value to what ultimately gets assigned to the ng-model value
        return viewValue.vv;
      });

      //$watch: our scope -> $viewValue
      scope.$watch('objectArray', function() {
        //replace the viewValue object:
        var newVv = {vv:scope.objectArray};
        if(!angular.equals(newVv, ngModelCtrl.$viewValue)) {
            ngModelCtrl.$setViewValue(newVv);
        }
      }, true); //deep watch

      //$render: $viewValue -> our scope
      ngModelCtrl.$render = function() {
        // console.log('ObjectTable: rendering viewValue to scope:', ngModelCtrl.$viewValue);
        //scope object can refer directly to the ngModel viewValue (just not the other way around!)
        scope.objectArray = ngModelCtrl.$viewValue.vv;
      };
    },


    controller: function ($scope) {

        var perspective = $scope.perspective;
        var objectMetaData = $scope.objectMetaData;
        var config = $scope.config || {};

        $scope.labels = NoonI18n.getBoLabelGroup(objectMetaData.class_name);




        //////////////////////////////////////////
        //Data Accessors
        //////////////////////////////////////////

        $scope.getValue = function(dataObj, field) {
            return _.get(dataObj, field);
        };

        $scope.getTypeDesc = function(field) {
            return objectMetaData.type_desc_map.getTypeDescriptor(field);
        };

        $scope.getFieldLabel = function(field) {
            var labels = $scope.labels;

            if(labels) {
                return (labels._abbreviated && labels._abbreviated[field]) || labels[field] || field;
            }

            return fieldName;
        };

        //////////////////////////////////////////
        // Column Sort/Group
        //////////////////////////////////////////
        //TODO sync up to the inital sort definition...

        $scope.allowSort = !!config.onSort;
        var columnDecorations = $scope.columnDecorations = {};

        var sortSeq = {
            asc:'desc',
            desc:'none',
            none:'asc'
        };

        var decorationMap = {
            asc:'fa-chevron-circle-down',
            desc:'fa-chevron-circle-up',
            none:''
        }

        if(perspective.sort) {
            var sd = perspective.sort;
            var sortFields = Object.keys(sd);
            for(var i=0; i < sortFields.length; i++) {
                var f = sortFields[i];
                columnDecorations[f] = decorationMap[sd[f]];
            }
        }

        if(config.onSort && !perspective.sort) {
            perspective.sort = {};
        }


        $scope.colHeaderClick = function($event, field) {
            var appendTerm = $event.shiftKey;
            var sd = perspective.sort;
            var currSortField = sd[field];

            if(!appendTerm) {
                var sortFields = Object.keys(sd);
                for(var i=0; i < sortFields.length; i++) {
                    var f = sortFields[i];
                    delete sd[f];
                    delete columnDecorations[f];
                }
            }

            if(currSortField) {
                sd[field] = sortSeq[currSortField];
            }
            else {
                sd[field] = sortSeq.none;
            }

            columnDecorations[field] = decorationMap[sd[field]];

            if(sd[field] === 'none') {
                delete sd[field];
            }

            if($scope.perspective.groupBy === field) {
                $scope.perspective.groupBy = null;
            }

            config.onSort();
        };


        $scope.colHeaderRightClick = function($event, field) {
            if(perspective.groupBy === field) {
                perspective.groupBy = null;
                columnDecorations[field] = '';
            }
            else {
                perspective.groupBy = field;
                columnDecorations[field] = 'fa-clone';
            }

            config.onSort();
        };



        //////////////////////////////////////////
        // Cell edit
        //////////////////////////////////////////

        var globalCellEdit = (config.cellEdit === true);
        var fieldSpecificEdit = config.cellEdit || {};

        var allowEdit = $scope.allowEdit = function(field) {
            return globalCellEdit || fieldSpecificEdit[field];
        };

        var editing = {};
        $scope.editing = function(obj, field) {
            return obj.$$hashKey === editing.item && field === editing.field;
        };

        $scope.cellClicked = function(obj, field) {
            if(allowEdit(field)) {
                editing = {
                    item:obj.$$hashKey,
                    field:field
                };
            }
        };


        //////////////////////////////////////////
        // Action invocatation: used for tableActions, recordActions
        //////////////////////////////////////////

        //Pull together recordActions and tableActions
        var specialActions = config.aliasActions || {};

        $scope.recordActions = config.recordActions || [];
        if(perspective.recordActions) {
            $scope.recordActions = DbuiAction.unaliasActionList($scope.recordActions.concat(perspective.recordActions), specialActions);
        }

        $scope.tableActions = config.tableActions || [];
        if(perspective.tableActions) {
            $scope.tableActions = DbuiAction.unaliasActionList($scope.tableActions.concat(perspective.tableActions), specialActions);
        }

        $scope.invokeRecordAction = function(dataObj, action, index) {
            //attach extra action "base parameters" from actionConfig 
            var params = {index:index, className:objectMetaData.class_name};
            if(config.baseParams) {
                _.assign(params, config.baseParams);
            }


            //Invoke via DbuiAction, including 'index' parameter
            return DbuiAction.invokeAction(perspective, dataObj, action, params);
        };

        $scope.invokeTableAction = $scope.invokeRecordAction.bind(null, null);

    }
  }
}

function ($scope, $stateParams, NoonI18n, DbuiAction, listPerspective) {

    var className = $scope.boClass = $stateParams.className;

    //Load the labels for this class's fields
    $scope.labels = NoonI18n.getBoLabelGroup(className);

    //Put listPerspective (populated in $state object's "resolve" block) into scope
    $scope.listPerspective = listPerspective;

    // function(perspectiveObj, contextBo, actionObj, argsObj)
    $scope.invokeAction = DbuiAction.invokeAction.bind(DbuiAction, listPerspective, null);

    if(listPerspective.actions) {
        $scope.actionList = DbuiAction.unaliasActionList(listPerspective.actions);
    }

    if(listPerspective.title) {
        $scope.setPageTitle(listPerspective.title);
    }
    else {
        $scope.setPageTitle('List '+className);
    }

}

function ($scope, $state, db, DbuiAlert, DbuiAction, NoonI18n, theObject, editPerspective,$rootScope) {

    var className = $scope.boClass = $state.params.className;
    $scope.theObject = theObject;
    $scope.editPerspective = editPerspective;
    var formStatus = $scope.formStatus = theObject._formStatus = {};

    var boId = $scope.boId = theObject && theObject._id;

    var title; 

    if(boId) {
        title = 'Edit '+theObject._disp;
    }
    else {
        title = 'New '+className;
    }

    $scope.setPageTitle(title);

    var deRegister = $rootScope.$on('$stateChangeStart', function(event, toState, toParams){
        if(formStatus.isDirty && !confirm('Are you sure you want to navigate away without saving?')){
            event.preventDefault();
        }
        else {
            deRegister();
        }
    });

    var saveTheObject = function() {
      console.log('saving object: ', $scope.theObject);

      $scope.theObject.save().then(
        function(result) {
          DbuiAlert.success('Successfully saved '+className+' "'+result._disp+'"');
          formStatus.isDirty = false;

          if(!boId) {
            $state.go('dbui.edit', {className:className, id:result._id, perspective:editPerspective.name});
          }
        },
        function(err) {
          DbuiAlert.danger('Problem saving: '+err);
        }

      );
    };

    var revert = function() {
      if(window.confirm('Are you sure?')) {
        theObject = $scope.theObject = db[className].findOne({_id:boId});

        //Need to re-bind the action invoker:
        $scope.invokeAction = DbuiAction.invokeAction.bind(DbuiAction, editPerspective, theObject);
      }
    };

    var specialActions = {
      save:{
        label:'Save',
        icon:'fa-save',
        fn: saveTheObject
      },
      revert:{
        label:'Revert',
        icon:'fa-undo',
        fn: revert
      }
    };


    $scope.labels = NoonI18n.getBoLabelGroup(className);


    // function(perspectiveObj, contextBo, actionObj, argsObj)
    $scope.invokeAction = DbuiAction.invokeAction.bind(DbuiAction, editPerspective, theObject);

    if(editPerspective.actions) {
        $scope.actionList = DbuiAction.unaliasActionList(editPerspective.actions);
    }

    if(editPerspective.recordActions) {
        $scope.recordActionList = DbuiAction.unaliasActionList(editPerspective.recordActions, specialActions);
    }


    if(editPerspective.onLoadAction) {
        DbuiAction.invokeAction(editPerspective, theObject, editPerspective.onLoadAction);
    }

  }

function ($http, $q, $rootScope, NoonWebService,DbuiFieldType, DbuiAction, NoonI18n, db, NoonConfig, $uibModal, DbuiUserPrefs) {


    /**
     * Dbui.init
     */
    this.init = function() {

        console.log('initializing Dbui');

        var instanceName = $rootScope.instanceName = 'NoonianDBUI'; 
        $rootScope.setPageTitle = function(title) {
            $rootScope.fullPageTitle = instanceName+(title ? ' | '+title : '');
            $rootScope.pageTitle = title;
        };

        NoonConfig.getParameter('sys.instanceName').then(function(cfgName) {
            if(cfgName) {
                instanceName = $rootScope.instanceName = cfgName;
            }
        });

        //init the alert queue
        $rootScope.alertMenu = {
            alerts:[],
            newAlerts:[],
            isOpen:false
        };

        return DbuiFieldType.init()
          .then(DbuiAction.init)
          .then(DbuiUserPrefs.init)
          .then(function() {
            return NoonWebService.call('dbui/getSidebarMenu').then(function(menuMap) {
                var currMenuKey = $rootScope.currMenuKey = DbuiUserPrefs.getParameter('selected_sidebar_menu') || menuMap._primary;

                $rootScope.sidebarMenu = menuMap[currMenuKey];
                $rootScope.sidebarMenuMap = menuMap;
                var labels = menuMap._labels;
                var menuList = [];
                _.forEach(menuMap, function(menuObj, menuKey) {
                    if(menuKey.indexOf('_') === 0) return;
                    menuList.push({
                        key:menuKey,
                        label:labels[menuKey] || menuKey
                    });
                });
                $rootScope.sidebarMenuList = _.sortBy(menuList, 'label');
            });
        });
    };

    /**
     * Dbui.switchSidebarMenu
     */
    this.switchSidebarMenu = function(key) {
        $rootScope.currMenuKey = key;
        $rootScope.sidebarMenu = $rootScope.sidebarMenuMap[key];
        DbuiUserPrefs.setParameter('selected_sidebar_menu', key);
    };

    /**
     * Dbui.getNavbarMenu
     */
    this.getNavbarMenu = function() {
        return NoonWebService.call('dbui/getNavbarMenu');
    };

    /**
     * Dbui.stringifyQueryClause
     */
    this.stringifyQueryClause = function(queryObj, className) {
        return NoonWebService.call('dbui/stringifyQuery', {className:className, queryObj:queryObj});
    };


    var roleMap;
    /**
     * Dbui.getRoleMap
     */
    this.getRoleMap = function() {
        var deferred = $q.defer();

        if(roleMap) {
            deferred.resolve(roleMap);
        }
        else {
            db.Role.find({}).$promise.then(function(roles){
                roleMap = {};
                _.forEach(roles, function(r) {
                    roleMap[r._id] = r.name;
                });
                deferred.resolve(roleMap);
            });
        }

        return deferred.promise;
    };


    /**
     * perspective.getEffectiveQuery
     * 
     */
    var getEffectiveQuery = function() {
      var textSearch = false;

      //From the text search
      if(this.searchString) {
        textSearch = {
          "$fulltextsearch":this.searchString
        };
      }

      if(this.filter) {
        if(textSearch) {
          return {"$and":[
            textSearch,
            this.filter
          ]};
        }
        else {
          return this.filter; //Just the query
        }
      }
      else { //No query...
        return textSearch || {}; //Just text search, if it exists
      }
    };


    /**
     * Dbui.normalizeLayout
     * @private
     * convert an 'abbreviated' version of a perspective layout (e.g. simple string array of field names)
     * into 'normalized' layout (array of rows) 
     */
    var normalizeLayout = this.normalizeLayout = function(layout) {
      //Crete an array of subsection objects
      var result = [];
      var currSection; //refers to the section being built as we iterate

      //First pass: convert into an array of "subsection" objects
      for(var i=0; i < layout.length; i++) {
        if(angular.isObject(layout[i]) && !angular.isArray(layout[i])) {
          //it's a non-array object: defines an explicit subsection
          currSection = null; //stop appending to the current one
          result.push(layout[i]);
        }
        else {
          if(!currSection) {
            //We're not currently building a section; initialize
            currSection = {rows:[]};
            result.push(currSection);
          }
          currSection.rows.push(layout[i]);
        }
      }

      //Second pass: convert all row elements to arrays
      for(i=0; i < result.length; i++) {
        var sectionRows = result[i].rows || [];
        for(var j=0; j< sectionRows.length; j++) {
          if(!angular.isArray(sectionRows[j])) {
            sectionRows[j] = [ sectionRows[j] ];
          }
        }
      }

      return result;
    };


    var perspectiveCache = {}; //cache[name/class/type] = {...}

    /**
     * Dbui.getPerspective
     */
    this.getPerspective = function(name, className, type) {

      var key = name+'/'+className+'/'+type;

      if(perspectiveCache[key]) {
        return $q.resolve(perspectiveCache[key]);
      }

      return NoonWebService.call('dbui/getPerspective', {name:name, class_name:className, type:type}).then(function(persp) {
        // console.log('GetPerspective', key, persp);
        persp.getEffectiveQuery = getEffectiveQuery;

        var dotPos = name.indexOf('.');
        if(dotPos > -1) {
            persp.name = name.substring(0, dotPos);
        }
        else {
            persp.name = name;
        }

        if(persp.layout) {
          persp.layout = normalizeLayout(persp.layout);
        }

        //NormalizeLayout for composites' "sub-perspectives"

        perspectiveCache[key] = persp;
        return persp;

      });

    };

    var customPageCache = {};
    this.getCustomPage = function(key) {

        var obj = customPageCache[key];

        if(obj) {
            return $q.resolve(obj);
        }
        else {
            return NoonWebService.call('dbui/getCustomPage', {key:key}).then(function(resultObj) {
                if(resultObj) {
                    customPageCache[key] = resultObj;
                }
                return resultObj;
            });
        }
    };


    /**
     * Show a dialog to get input from the user.
     * content of dialog determined by provided type descriptor map and perspective
     */
    this.showDialog = function(title, prompt, tdMap, perspective, initObj) {

        var modalInstance;
        var scope = $rootScope.$new(true);

        scope.title = title;
        scope.prompt = prompt;
        scope.perspective = perspective;
        scope.theObject = initObj || {};

        perspective.layout = normalizeLayout(perspective.layout);

        scope.theObject._bo_meta_data = {type_desc_map: tdMap};
        if(!tdMap.getTypeDescriptor) {
            tdMap.getTypeDescriptor = function(f) {
                return tdMap[f];
            };
        }

        return $uibModal.open({
            templateUrl:'dbui/reusable/core/object_editor_dialog.html',
            size:'lg',
            scope: scope
        }).result;
    };


    /**
     *  Used to determine which bootstrap classes should be used when 
     *  displaying forms w/ dynamic column counts
     */
    this.columnClasses = {
          top:[
            'col-md-12',
            'col-md-12',
            'col-md-6',
            'col-md-4',
            'col-md-3'
          ],
          label:[
            '',
            'col-sm-2',
            'col-sm-4',
            'col-sm-6',
            'col-sm-6'
          ],
          value:[
            '',
            'col-sm-10',
            'col-sm-8',
            'col-sm-6',
            'col-sm-6'
          ],
        };

}

function (Dbui, $uibModal, $rootScope) {
  return {
    /**
     *  Use uibModal to show a datatable alowing the selection of one or multiple BO's
     **/
    showDialog: function(theObject, perspective, title) {


