function (NoonConfig, $rootScope) {

    var PREFS_KEY='sys.user.preferences';
    var prefs = {};

    /**
     * Dbui.init
     */
    this.init = function() {
        console.log('initializing DbuiUserPrefs');

        return NoonConfig.getParameter(PREFS_KEY).then(function(result) {
            $rootScope.userPrefs = prefs = result || {};
        }, 
        function(err) {
