}

function (Dbui, I18n) {
  return {

    templateUrl: 'dbui/reusable/core/object_editor.html',

    restrict: 'E',
    scope: {
      theObject: '=',  //Object being displayed (a model instance from datsource)
      perspective: '=',
      formStatus: '=?'
    },

    link:function(scope) {

        scope.$watch('editorForm.$dirty', function(isDirty) {
            //Set isDirty to true when we the form becomes dirty;
            // (unset is done externally)

            if(scope.formStatus && isDirty) {
                scope.formStatus.isDirty = true;
            }
        });

        scope.$watch('formStatus.isDirty', function(isDirty) {
            if(!isDirty) {
                scope.editorForm.$setPristine();
            }
        });

        //the dbui-field-editor updates linkStatus when it's link function is complete
        // use that to initialize "pristine/dirty" status of the form
        // (the async stuff in the field editor's link fn seems to make the ngModel dirty/pristine stuff act funky)
        scope.linkStatus = {};
        scope.$watch('linkStatus', function(ls) {
            scope.editorForm.$setPristine();
            if(scope.formStatus) scope.formStatus.isDirty = false;
        }, true);
    },

    controller:function($scope, $parse) {

      var theObject = $scope.theObject;
      var perspective = $scope.perspective;

      var className = theObject._bo_meta_data.class_name;

      $scope.labelGroup = theObject._bo_meta_data.field_labels;

      $scope.typeDescMap = theObject._bo_meta_data.type_desc_map;


      $scope.colClass = Dbui.columnClasses;

      //Set up getter/setter function with ngModel;
      // allows us to have dotted subfields in the layout,
      // resulting in editable subfields
      var getterSetterFn = function(fieldName, value) {

        if(arguments.length > 1) {
          //called as setter
          // console.log('setter!!!!!!!', fieldName, value);
          _.set(theObject, fieldName, value);
        }

        return _.get(theObject, fieldName);

      };

      var getterSetter = $scope.getterSetter = {};

      //Traverse the normalized layout: section -> rows -> field names
      _.forEach(perspective.layout, function(section) {
        _.forEach(section.rows, function(row) {
          for(var i=0; i < row.length; i++) {
            var f = row[i];
            getterSetter[f] = getterSetterFn.bind(null, f);
          }
        });
      });


      var fieldCustomizations = perspective.fieldCustomizations || {};
      var displayCheckers = {};
      var contextFields = {}; //maps dotted field names present in fieldCustomizations to the base field name


      var requiredFields = {};
      var reqExpression = '';
      var reqChecker;
      var reqWatcher = function() {
          $scope.formStatus.isValid = !!reqChecker($scope.theObject);
      };

      for(var f in fieldCustomizations) {
        if(fieldCustomizations[f].conditionalDisplay) {
          displayCheckers[f] = $parse(fieldCustomizations[f].conditionalDisplay);
          var dotPos = f.indexOf('.');
          if(dotPos > -1) {
              contextFields[f] = f.substring(0, dotPos);
          }
        }

        if(fieldCustomizations[f].required) {
            requiredFields[f] = true;
            reqExpression += (reqExpression ? ' && ' : '')+f;
            $scope.$watch('theObject.'+f, reqWatcher);
        } 
      }

      if(reqExpression) {
          reqChecker = $parse(reqExpression);
      }

      $scope.isRequired = function(field) {
          if(!requiredFields[field]) {
              return false;
          }
          else {
              return !_.get($scope.theObject, field);
          }
      };

      $scope.shouldShow  = function(field) {
          if(!$scope.getTypeDesc(field)) {
              return false;
          }
          var dc = displayCheckers[field];
          if(!dc) {
              return true;
          }
          else {
              var context = contextFields[field] ? _.get($scope.theObject, contextFields[field]) : $scope.theObject;
              return dc(context);
          }
      };

      var tdCache = {};
      $scope.getTypeDesc = function(field) {
          if(!tdCache[field]) {
              tdCache[field] = $scope.typeDescMap.getTypeDescriptor(field);
          }

          return tdCache[field];
      };

    }
  };
}

function ($rootScope, $timeout) {

    var THIS = this;

    var closeDbuiAlert = function(alertObj) {
        var arr = $rootScope.alertMenu.alerts;

        for(var i=0; i < arr.length; i++) {
            if(arr[i] === alertObj) {
                arr.splice(i, 1);
                break;
            }
        }

        if(arr.length === 0) {
            $rootScope.alertMenu.isOpen = false;
        }
        $rootScope.alertMenu.newAlerts.splice(0, $rootScope.alertMenu.newAlerts.length);
    };

    var dismissDbuiAlerts = function() {
        var arr = $rootScope.alertMenu.alerts;
        arr.splice(0, arr.length);
        $rootScope.alertMenu.isOpen = false;
    };

    var lastTimeout;

    this.alert = function(type, message, timeout) {
        console.log('DbuiAlert.alert', type, message, timeout);
        if(!$rootScope.closeDbuiAlert) {
            $rootScope.closeDbuiAlert = closeDbuiAlert;
            $rootScope.dismissDbuiAlerts = dismissDbuiAlerts;
        }

        if(!timeout) {
            timeout = 5000;
        }

        var alertObj = {
            msg:message,
            type:type,
            dismiss_timeout:timeout
        };

        // $rootScope.dbui_alerts.push(alertObj);
        $rootScope.alertMenu.alerts.unshift(alertObj);
        $rootScope.alertMenu.newAlerts.unshift(alertObj);
        $rootScope.alertMenu.showQueue = true;


        if(lastTimeout) {
            $timeout.cancel(lastTimeout);
        }

        lastTimeout = $timeout(function() {
            $rootScope.alertMenu.showQueue = false;
            $rootScope.alertMenu.newAlerts.splice(0, $rootScope.alertMenu.newAlerts.length);
            lastTimeout = false;
        }, timeout);


    };

    this.info = function(msg, timeout) {
        THIS.alert('info', msg, timeout);
    };

    this.success = function(msg, timeout) {
        THIS.alert('success', msg, timeout);
    };

    this.warning = function(msg, timeout) {
        THIS.alert('warning', msg, timeout);
    };

    this.danger = function(msg, timeout) {
        THIS.alert('danger', msg, timeout);
    };


}

function ($rootScope) {
    return {
        templateUrl:'dbui/core/breadcrumbar.html',

        restrict: 'E',


        controller:function($scope, $state) {


            //track "crumbs" as 
            var crumbHistory = $scope.crumbHistory = [];
            var findCrumb = function(s, sp) {
                for(var i=0; i < crumbHistory.length; i++) {
                    var c = crumbHistory[i];
                    if(angular.equals(c.state, s) && angular.equals(c.stateParams, sp)) {
                        return i;
                    }
                }
                return -1;
            };

            $rootScope.$on('$stateChangeSuccess', function(e, toState, toParams, fromState, fromParams) {
                //console.log(crumbHistory);
                var existingPos = findCrumb(toState, toParams);
                if(existingPos > -1) {
                    $scope.currentCrumb = crumbHistory[existingPos];
                }
                else {
                    var prevCrumb = $scope.currentCrumb || {};
                    var newCrumb = $scope.currentCrumb = {
                        state:toState,
                        stateParams:toParams,
                        title:$rootScope.pageTitle,
                        pos:crumbHistory.length
                    };


                    if(prevCrumb.pos < crumbHistory.length-1) {
                        var savedPins = [];
                        for(var i=prevCrumb.pos+1; i < crumbHistory.length; i++ ) {
                            var curr = crumbHistory[i];
                            if(curr.pinned) {
                                savedPins.push(curr);
                            }
                        }
                        console.log('savedPins', savedPins);

                        crumbHistory.splice(prevCrumb.pos+1, crumbHistory.length);

                        newCrumb.pos = crumbHistory.length

                        crumbHistory.push(newCrumb);

                        var newPos = crumbHistory.length;
                        _.forEach(savedPins, function(c) {
                            c.pos = newPos++;
                            crumbHistory.push(c);
                        });

                    }
                    else {
                        crumbHistory.push(newCrumb);
                    }



                }
            }); 

            $rootScope.$watch('pageTitle', function(newTitle) {
                if($scope.currentCrumb) {
                    $scope.currentCrumb.title = newTitle;
                }
            });

            $scope.selected = function(c) {
                $state.go(c.state, c.stateParams);
            };

            $scope.togglepin = function(c) {
                c.pinned = !c.pinned;
            };
        }
    }
}

function () {
    return function (value) {
      if (!value) { return ''; }

      var strVal = ''+value;
      var dollars = '';
      var cents = '';
      var dotPos = strVal.indexOf('.');
      if(dotPos > -1) {
        dollars = strVal.substring(0, dotPos);
        cents = strVal.substring(dotPos+1);
        while(cents.length < 2) {
          cents += '0';
        }
        cents = '.'+cents;
      }
      else {
        dollars = strVal;
      }

      //Separate into thousands:
      var dollarsSep = '';
      var comma = '';
      var i = dollars.length;

      while(i-3 >= 0) {
        var currPiece = dollars.substring(i-3, i);
        dollarsSep = currPiece+comma+dollarsSep;
        comma = ',';
        i-=3;
      }

      if(i !== 0) {
        var finalPiece = dollars.substring(0, i);
        dollarsSep = finalPiece + comma + dollarsSep;
      }

      return '$'+dollarsSep+cents;
    };
  }

