function () {
    var db;

    /**
     * Constructor for type descriptor maps: special dynamic versions of BO definitions
     * @constructor
     * @param typeDescMap
     * @param {!Object.<string, Object>} fieldLabels
     **/
    var BusinessObjectLabelGroup = function(typeDescMap, fieldLabels) {
        if(!this || !(this instanceof BusinessObjectLabelGroup)) {
            return new BusinessObjectLabelGroup(typeDescMap, fieldLabels);
        }
        this.__typeDescMap = typeDescMap;
        _.assign(this, fieldLabels);

        var myLabels = this;

        //Process any composites; recursively instantiate BOLabelGroups as appropriate
        _.forEach(typeDescMap, function(td, fieldName) {
            if(td.type === 'composite' || (td instanceof Array && td[0].type === 'composite')) {
                var myTd = td instanceof Array ? td[0] : td;
                var subLabelgroupKey = '#'+fieldName;
                if(myTd.type_desc_map &&  myLabels[subLabelgroupKey]) {
                    myLabels[subLabelgroupKey] = new BusinessObjectLabelGroup(myTd.type_desc_map, myLabels[subLabelgroupKey]);
                }
            }
        });
    };

    /**
     * @private 
     * getTypeDescriptor may need access to db when traversing into reference fields.  
     * However, simply injecting db as a factory causes a circular depenency.
     */
    BusinessObjectLabelGroup._init = function(dbService) {
        db = dbService;
    };

    /**
     * @function BusinessObjectLabelGroup#getLabel
     * Retrieves a label for a particular field/sub-field; recursing into composites and references if needed.
     * @param {string} path can be a simple fieldname or dotted into reference or composite fields, e.g.:
     *     db.SomeBusinessObj._bo_meta_data.field_labels.getTypeDescriptor('refField.blah');
     **/
    Object.defineProperty(BusinessObjectLabelGroup.prototype, 'getLabel', {
        enumerable:false, writable:false,
        value:function(path, abbreviated) {
            var dotPos = path.indexOf('.');

            if(dotPos === -1) { //no dot -> just a field name
                if(abbreviated && this._abbreviated && this._abbreviated[path]) {
                    return this._abbreviated[path];
                }
                return this[path] || path;
            }

            //Peel off the first piece.
            var localField = path.substring(0, dotPos);
            var subPath = path.substring(dotPos+1);

            var localTd = this.__typeDescMap[localField];

            if(!localTd) {
              console.error('invalid fieldname for td', localField, this);
              return path;
            }

            if(localTd instanceof Array && localTd.length > 0) {
                localTd = localTd[0];
            } 

            if(localTd.type === 'reference') {
                //Grab label it from referenced db model
                var RefModel = db[localTd.ref_class];
                if(!RefModel) {
                    console.error('invalid reference class in type descriptor:', this);
                    return path;
                }

                return RefModel._bo_meta_data.field_labels.getLabel(subPath, abbreviated);
            }
            else if(localTd.type === 'composite') {
                var subLabelgroupKey = '#'+fieldName;

                if(!this.fieldLabels[subLabelgroupKey]) {
                    console.error('labelgroup missing for composite field', this);
                    return path;
                }

                return this.fieldLabels[subLabelgroupKey].getLabel(subPath, abbreviated);
            }
            else {
              //dotted into a non-reference/composite or a non-existent field:
              console.log('invalid field specifier for this label group', this, subPath);
              return path;
            }

          } //end function
      });



    return BusinessObjectLabelGroup;
}

business_objects.doc:
Renders a tabbed set of BusinessObject tables related to a particular object.  
@param {BusinessObject} theObject - the object to whom the displayed tables are related
@param {string} [perspectiveName=default] - the name of the perspective to use for rendering the tables (can be overridden by individual relatedSpec)
@param {Object[]} relatedSpec - specify which references to theObject are of interest
@param {string} relatedSpec[].class - name of BusinessObject class containing reference to theObject
@param {string} relatedSpec[].field - reference field of interest
@param {string} [relatedSpec[].perspective] - name of perspective for displaying this

business_objects.function:
function (Dbui, $q, I18n) {
  return {
    templateUrl: 'dbui/reusable/core/relatedviewer.html',
    restrict: 'E',
    scope: {
      theObject: '<',
      perspectiveName: '<?',
      relatedSpec: '<'
    },

    controller:function($scope) {

      var theObject = $scope.theObject;
      var perspectiveName = $scope.perspectiveName;

      var className = theObject._bo_meta_data.class_name;


      if(!perspectiveName)
        perspectiveName = $scope.perspectiveName = 'default';


      $scope.labels = I18n.getBoLabelGroup(className);
      $scope.labels.$promise
      .then(function() {
          var related = $scope.relatedSpec;

        //Build up $scope.relatedTables[title,class,perspective]
        var perspectivePromises = [];
        for(var i=0; i < related.length; i++) {
            var usePerspective = related[i].perspective || perspectiveName;
            perspectivePromises.push(Dbui.getPerspective(usePerspective+'.related['+className+'_'+related[i].field+']', related[i].class, 'list'));
        }

        $q.all(perspectivePromises).then(function(perspectiveArr) {
          var relatedTables = $scope.relatedTables = [];
          for(var i=0; i < perspectiveArr.length; i++) {
            var r = related[i];  //class and field
            var rp = perspectiveArr[i]; //full list perspective

            //since we may have a cached version of the perspective from Dbui.getPerspective, 
            // we need to avoid augmenting the filter multiple times!
            if(!rp.refFilter) {
                rp.refFilter = {};

                //apply it to the filter that will be used by <dbui-business-object-browser> directive
                if(rp.filter) {
                    rp.filter = {$and:[rp.refFilter, rp.filter]};
                }
                else {
                    rp.filter = rp.refFilter;
                }
            }

            rp.refFilter[r.field+'._id'] = theObject._id;

            var label = rp.title || $scope.labels['_related_'+r.class+'_'+r.field] || r.class;

            relatedTables.push({
              title:label,
              class:r.class,
              perspective:rp
            });

          }


        });
      });

    }
  };
}

function (NoonWebService, NoonI18n, db, $q) {
    var THIS = this;

    //Map fieldtype name to appropriate FieldTypeUiSpec object
    var viewSpecs = {};
    var editSpecs = {};
    var viewArrSpecs = {};
    var editArrSpecs = {};

    var specMaps = {view:viewSpecs, edit:editSpecs};
    var arrSpecMaps = {view:viewArrSpecs, edit:editArrSpecs};

    var queryOpLabels;
    var queryOpMap = {}; //Map fieldtype name to list of queryop

    var cachePromises = {}; //keep track of classes for whom we've called cache()

    /**
     * DbuiFieldType.init
     * 
     */
    this.init = function() {
        console.log('initializing DbuiFieldType'); 

        //Pull in queryop metadata
        return NoonWebService.call('dbui/getQueryOpMetadata').then(function(result) {

            _.forEach(result, function(editorSpec) {
                _.forEach(editorSpec.types, function(typeName) {
                   queryOpMap[typeName] = editorSpec.queryops; 
                });
            });

        });

    };

    /**
     * 
     * @private
     */
    var getTypeName = function(typeDesc) {
        if(typeDesc instanceof Array) {
            return typeDesc[0].type+'[]';
        }
        else if(typeDesc) {
            return typeDesc.type;
        }
    };

    var applicableWildcard = function(typeDesc) {
        return typeDesc instanceof Array ? '*[]' : '*';
    };

    /**
     * DbuiFieldType.cacheTypeInfo
     * call getFieldTypeMetadata to cache templates/controllers/etc. for a fieldtype or fieldtypes of a specific class
     * @private
     */
    var cacheTypeInfo = function (wsParams, viewEdit) {
        // console.log('cacheTypeInfo', wsParams, viewEdit);
        return NoonWebService.call('dbui/getFieldTypeMetadata', wsParams).then(function(resultObj) {

            _.forEach(resultObj.result, function(specObj) {
                var typeList;
                if(specObj.fieldtypes && specObj.fieldtypes.length) {
                    typeList = specObj.fieldtypes;
                }
                else {
                    typeList = ['*'];
                }

                var specMap;
                if(specObj.for_array) {
                    specMap = arrSpecMaps[viewEdit];
                }
                else {
                    specMap = specMaps[viewEdit];
                }

                _.forEach(typeList, function(typeName) {
                    // console.log('saving template for ', typeName);
                    if(!specMap[typeName]) {
                        specMap[typeName] = {};
                    }
                    if(specObj.key.indexOf('dbui.core') === 0) {
                        specMap[typeName].default = specObj;
                    }
                    else {
                        specMap[typeName][specObj.key] = specObj;
                    }
                });
            });

        },
        function(err) {
            console.error('Bad result for cacheTypeInfo ', wsParams, err);
        });
    };

    /**
     * Point type name to wildcard spec if type-specific spec isn't cached
     */
    var setCacheWildcardIfNeeded = function(typeDesc, viewEdit) {
        var typeName;
        var specMap;
        if(typeDesc instanceof Array) {
            specMap = arrSpecMaps[viewEdit];
            typeName = typeDesc[0].type;
        }
        else {
            specMap = specMaps[viewEdit];
            typeName = typeDesc.type;
        }

        if(!specMap[typeName] && specMap['*']) {
            specMap[typeName] = specMap['*'];
        }
    };

    /** 
     * DbuiFieldType.cacheTypeInfoForFieldtype
     * Retrieves and caches FieldTypeUiSpec data for fields of specified fieldtype
     */
    var cacheTypeInfoForFieldtype = function(typeDesc, viewEdit) {
        var typeName = getTypeName(typeDesc);
        var promiseCacheKey = typeName+'|'+viewEdit;

        if(!cachePromises[promiseCacheKey]) {
            cachePromises[promiseCacheKey] = cacheTypeInfo( {field_type:typeName,view_or_edit:viewEdit}, viewEdit )
                .then(setCacheWildcardIfNeeded.bind(null, typeDesc, viewEdit));
        }

        return cachePromises[promiseCacheKey];
    };

    /** 
     * DbuiFieldType.cacheTypeInfoForClass
     * Retrieves and caches FieldTypeUiSpec data for fields of specified class
     */
    this.cacheTypeInfoForClass = function(className) {
        if(!cachePromises[className]) {
            cachePromises[className] = cacheTypeInfo({class_name:className,view_or_edit:'view'}, 'view')
                .then(cacheTypeInfo.bind(null, {class_name:className,view_or_edit:'edit'}, 'edit'))
                .then(function() {
                    var typeDescMap = db[className]._bo_meta_data.type_desc_map;
                    _.forEach(typeDescMap, function(typeDesc) {
                        setCacheWildcardIfNeeded(typeDesc, 'view');
                        setCacheWildcardIfNeeded(typeDesc, 'edit');
                    });
                });
        }
        return cachePromises[className];
    };


    var getSpecFromCache = function(typeDesc, viewEdit) {
        var typeName;
        var specMap;
        if(typeDesc instanceof Array) {
            specMap = arrSpecMaps[viewEdit];
            typeName = typeDesc[0].type;
        }
        else {
            specMap = specMaps[viewEdit];
            typeName = typeDesc.type;
        }

        if(specMap[typeName]) {
           return specMap[typeName];
        }
    };

    /**
     * DbuiFieldType.getSpec
     */
    this.getSpec = function(typeDesc, viewEdit, fieldCustomizations) {
        var fc = fieldCustomizations || {};
        var specKey = fc.uiSpec || 'default';


        var specObj = getSpecFromCache(typeDesc, viewEdit);
        if(specObj) {
            return $q.resolve(specObj[specKey]);
        }

        //Not cached, retrieve it from server:
        return cacheTypeInfoForFieldtype(typeDesc, viewEdit).then(function() {
            return getSpecFromCache(typeDesc, viewEdit)[specKey];
        });

    };


    /**
     * 
     */
    var getOpList =
    this.getOpList = function(typeDesc) {
        var typeName = getTypeName(typeDesc);
        if(queryOpMap[typeName]) {
            return queryOpMap[typeName];
        }
        else {
            var wc = applicableWildcard(typeDesc);
            if(queryOpMap[wc]) {
                return queryOpMap[wc];
            }
        }
    };

    /**
     * 
     */
    this.getOpInfo = function(typeDesc, opName) {
        var opInfoList = getOpList(typeDesc);
        for(var i=0; i < opInfoList.length; i++) {
            if(opName === opInfoList[i].op) {
                return opInfoList[i];
            }
        }
    };




    var expandRefPlaceholder = function(fieldList, boLabelGroup, denormOnly) {
        var myPos;
        for(myPos=0; myPos < fieldList.length ; myPos++) {
          if(fieldList[myPos] === this)
            break;
        }
        //Remove the placeholder
        fieldList.splice(myPos, 1);

        //Insert the items for the ref fields
        var namePrefix = this.fieldName;
        var refTd = db[this.td.ref_class]._bo_meta_data.type_desc_map;

        var refFieldList = denormOnly ? (this.td.denormalize_fields || []) : Object.keys(refTd);

        for(var i=0; i < refFieldList.length; i++) {
          var refFieldName = refFieldList[i];
          var qualifiedSubfield = namePrefix+refFieldName;
          var fi = {
            fieldName:qualifiedSubfield,
            fieldLabel: boLabelGroup[namePrefix.substring(0, namePrefix.length-1)]+'.'+(boLabelGroup[qualifiedSubfield] || refFieldName),
            td:refTd[refFieldName]
          };

          fieldList.splice(myPos, 0, fi);
        }

      };

    /**
     * DbuiFieldType.getAugmentedFieldList
     *  returns a promise -> array of objects describing the fields of bo className
     *  {
     *    fieldName:'field name - dotted if ref sub-field',
     *    fieldLabel:'from i18n getBoLabelGroup, for language of current user',
     *    td: typedesc object copied from bo metadata
     *    refPlaceholder: boolean - true if this is a placeholder for a reference field,
     *    expand: function to expand this placeholder into entries for reference fields
     *  }
     **/
    this.getAugmentedFieldList = function(className, denormOnly, searchableOnly) {

      return NoonI18n.getBoLabelGroup(className).$promise.then(function(boLabelGroup) {
        var Model = db[className];
        var typeDescMap = Model._bo_meta_data.type_desc_map;
        var resultList = [];

        boLabelGroup._abbreviated = boLabelGroup._abbreviated || {};

        for(var f in typeDescMap) {
          if(f.indexOf('_') === 0)
            continue;

          var td = typeDescMap[f];

          if(searchableOnly && !getOpList(td)) 
            continue;


          var fieldLabel = boLabelGroup._abbreviated[f] || boLabelGroup[f] || f;
          var fieldInfo = {
            fieldName:f,
            fieldLabel:fieldLabel,
            td:td
          };
          resultList.push(fieldInfo);

          if(td.type === 'reference' && (!denormOnly || td.denormalize_fields)) {
            var fiPlaceholder = {
              fieldName:f+'.',
              fieldLabel:fieldLabel+'...',
              td:td,
              refPlaceholder:true
            };

            fiPlaceholder.expand = expandRefPlaceholder.bind(fiPlaceholder, resultList, boLabelGroup, denormOnly);
            resultList.push(fiPlaceholder);
          }

        }//end typeDescMap iteration

        resultList.sort(function(a, b) {
          if (a.fieldLabel < b.fieldLabel)
            return -1;
          if (a.fieldLabel > b.fieldLabel)
            return 1;
          return 0;
        });


        return resultList;
      });


    };


}

function ($http, $q) {

    var doCall = function(conf) {
      var deferred = $q.defer();

      $http(conf).then(
        function(resp) {

          if(resp.data.error) {
            deferred.reject(resp.data.error);
          }
          else {
            deferred.resolve(resp.data);
          }
        },
        function(resp) { //An http error code was returned...
          if(resp.data.error)
            deferred.reject(resp.data.error);
          else
            deferred.reject(resp.data);
        }
      );

      return deferred.promise;
    };

    var massagePath = function(path) {
    //   if(path.indexOf('/sys/') ===0 )
    //       return path.substring(5);
    //     else
          return 'ws/'+path;
    }


    return {

      call: function(path, params) {
        return doCall({method:'GET', url:massagePath(path), params:params});
      },

      post: function(path, params, postObj) {
        return doCall({method:'POST', url:massagePath(path), params:params, data:postObj});
      }

    };

  }

