                  }
                  catch(err) {
                      console.error('bad controller function for customPage', $scope.key, err);
                  }
              }

          });
      }

    };
  }

function ($rootScope, $scope, $location, $state, db, Auth, Action, Dbui, NoonConfig) {

    $scope.loggedIn = false;
    $scope.isAdmin = false;

    $scope.appTitle = $rootScope.instanceName;

    Auth.onLogin( function(evt, userObj) {

      $scope.loggedIn = true;
      $scope.currentUser = userObj;
      $scope.isAdmin = userObj.isAdmin;


      if($scope.isAdmin) {
        //TODO: eventually want to allow for non-admin navbar menu!
        Dbui.getNavbarMenu().then(function(navbarMenu) {
            $scope.navbarMenu  = navbarMenu;
        });

        var pkgConfig;
        NoonConfig.getParameter('sys.enablePackaging').then(function(value) {
            $scope.enablePackaging = value;
            if(value) {
                db.BusinessObjectPackage.find({}, null, {sort:{name:1}}).$promise.then(function(result) {
                $scope.pkgList = [{key:false, name:'*none*'}].concat(result);

                db.Config.findOne({key:'sys.currentPackage'}).$promise.then(function(cfgObj) {
                    pkgConfig = cfgObj;
                    for(var i=0; i < $scope.pkgList.length; i++)
                      if($scope.pkgList[i].key == pkgConfig.value) {
                        $scope.currPkg = $scope.pkgList[i];
                      }
                  });
                });

            }
        });


        $scope.switchPkg = function(pkg) {
          pkgConfig.value = pkg.key;
          pkgConfig.save().then(function() {
            $scope.currPkg = pkg;
          },
          function(err) {
            alert('Problem saving change: '+err);
          });
        };

      }


      //Redirect to home if we're looking at the login screen:
      if($location.path() === '/login')
        $location.path('/');
    });

    Auth.onLogout( function() {
      $scope.loggedIn = false;
      $scope.isAdmin = false;
    });


    $scope.isCollapsed = false;


    $scope.logout = function() {
      Auth.logout();
      window.location.reload();
    };

    $scope.isActive = function(stateName) {
      return stateName === $state.$current.name;
    };


    $scope.switchMenu = Dbui.switchSidebarMenu;

    $scope.invokeAction = function(action) {
        $scope.boMenuOpen = false;
      Action.invoke(action);
    };

    $scope.appTitleRightClick = function() {
        var myUrl = $location.absUrl();
        window.open(myUrl);
    };

    $scope.toggleSidebar = function() {
        $rootScope.sidebarCollapsed = !$rootScope.sidebarCollapsed;    
    };
  }

function ($parse, $http) {
    return {
      restrict: 'A',
      link: function(scope, element, attrs) {
        var model = $parse(attrs.attachmentUploader);
        var modelSetter = model.assign;

        scope.initiateUpload = function() {
          element.click();
        }

        element.bind('change', function() {
          scope.$apply(function() {
            //A file has been selected

            var file = element[0].files[0];

            var metaObj = {
              filename:file.name,
              size:file.size,
              type:file.type
            };

            var fd = new FormData();
            fd.append('metadata', JSON.stringify(metaObj));
            fd.append('file', file);

            scope.uploading = true;

            var httpConfig = {
              transformRequest: angular.identity,
              headers: {'Content-Type': undefined}
            };

            var uploadWs = 'attachment_ws/upload';
            if(scope.typeDesc.mode) {
              uploadWs += '?mode='+scope.typeDesc.mode+'&resource_path='+(scope.typeDesc.resource_path || '');
            }

            $http.post(uploadWs, fd, httpConfig)
            .then(function(result) {
              scope.uploading = false;

              modelSetter(scope, result.data.result);
            },
            function(err) {
              console.log(err);
            });
          });
        });

        scope.clearFile = function() {
          element[0].value = '';
        }
      }
    };
  }

function ($scope, Config, Action) {
    //Get the config parameter
    $scope.loading = true;
    Config.getParameter('sys.dbui.homeAction').then(
      function(homeAction) {
        Action.invoke(homeAction);
      },
      function(err) {
        $scope.loading = false;
        console.log(err);
        $scope.message = "Home action not properly configured. Be sure to set config parameter 'dbui.homeAction'";
      }
    );
  }

business_objects.doc:
A graphical tool for building a query against a specific business object class.  It allows construction of (a) ANDed together groups of ORed terms, and (b) ORed together groups of ANDed terms.

Query object bound via ngModel.

@todo allow switching to a "raw" mode - json object editor - to allow manual typing of arbitrary query

business_objects.function:
function (DbuiFieldType, db) {
    return {
      templateUrl: 'dbui/reusable/core/querybuilder.html',
      restrict: 'E',
      scope: {
        boClass: '<', //The full field name -> type descriptor map (definition from BusinessObjectDef)
        onClose: '&'
      },
      require: 'ngModel',

      link: function(scope, element, attrs, ngModel) {



        var oppositeConj = {$and:'$or', $or:'$and'};

        var isCompatibleQuery = function(query) {
          //TODO: make sure query is one outer and/or containing opposite terms
          // if incompatible -> viewValue is {plainQuery:'...'}, edited w/ object editor.
        };

        var isConjunction = function(key) {
          return !!oppositeConj[key];
        };

        var getKey = function(term) {
          var keyArr = Object.keys(term);
          if(keyArr.length === 0)
            return null;
          else
            return keyArr[0];
        };

        //Normalize queries that use the shorthand "$and", e.g.
        // {field1:cond, field2:cond2} -> {$and:[{field1:cond},{field2:cond2}]}
        var longhandAnd = function(queryObj) {
            var keyArr = Object.keys(queryObj);
            if(keyArr.length > 1) {
                var termArr = [];
                _.forEach(keyArr, function(k) {
                    var t = {};
                    t[k] = queryObj[k];
                    termArr.push(t);
                });
                return {$and:termArr};
            }
            else {
                return queryObj;
            }
        };


        //$formatter: query -> $viewValue
        ngModel.$formatters.push(function(queryObj) {
        //   console.log('QB: formatting this query to viewValue: ', queryObj);

          if(!queryObj)
            return null;

          queryObj = longhandAnd(queryObj);    

          var outerKey = getKey(queryObj);

          if(!outerKey)
            return {};

          if(!isConjunction(outerKey)) {
            //Single term query -> one clauseGroup consisting of the solitary term
            return {clauseGroups:[[queryObj]]};
          }

          //Now we know we're dealing w/ an and/or query...
          var vv = {};
          var outerTerms = queryObj[outerKey];

          //... but are there multiple clause groups?
          var multiGroup = false;
          for(var i=0; i < outerTerms.length; i++) {
             var innerKey = getKey(outerTerms[i]);
             if(isConjunction(innerKey)) {
              multiGroup = true;
              vv.outerConj = outerKey;
              vv.innerConj = innerKey;
              break;
             }
          }

          if(!multiGroup) {
            vv.inerConj = outerKey;
            vv.outerConj = oppositeConj[outerKey];
            //outerTerms should be pulled into one clause expression:
            vv.clauseGroups = [outerTerms];
          }
          else {
            //multi group: each item in outerTerms is treated as one clauseGroup
            vv.clauseGroups = [];
            for(var i=0; i < outerTerms.length; i++) {
              var innerKey = getKey(outerTerms[i]);
              if(isConjunction(innerKey)) {
                var innerTerms = outerTerms[i][innerKey];
                vv.clauseGroups.push(innerTerms);
              }
              else {
                vv.clauseGroups.push([outerTerms[i]]);
              }
            }
          }
          // console.log('...resulting vv: ', vv);
          return vv;

        });

        //$parser: $viewValue -> query term
        ngModel.$parsers.push(function(vv) {
          // console.log('QB: parsing view value: ', vv);
          if(!vv || !vv.clauseGroups)
            return null;

          if(vv.plainQuery)
            return vv.plainQuery;

          //Single term:
          if(vv.clauseGroups.length == 1 && vv.clauseGroups[0].length == 1) {
            return vv.clauseGroups[0][0];
          }


          //Turn clauseGroups into a query!
          var query = {};
          var outerTerms = query[vv.outerConj] = [];

          for(var i=0; i < vv.clauseGroups.length; i++) {
            var clauseGroup = vv.clauseGroups[i];
            if(clauseGroup.length == 1) {
              outerTerms.push(clauseGroup[0]);
            }
            else {
              var innerClause = {};
              innerClause[vv.innerConj] = clauseGroup;
              outerTerms.push(innerClause);
            }
          }

          // console.log('...resulting query: ', outerTerms.length===1 ? outerTerms[0] : query);

          if(outerTerms.length == 1)
            return outerTerms[0]; // don't need the outer conj
          else
            return query;
        });

        //$watch: our scope -> $viewValue
        // watch via function due to complexity of watching an array of arrays...
        // this function flattens all objects in to a single array for angular to examine
        var flattenedClauseGroups = function() {
          var cg = scope.queryModel ? scope.queryModel.clauseGroups : null;
          if(!cg)
            return null;

          var flattened = [];
          for(var i=0; i < cg.length; i++) {
            for(var j=0; j < cg[i].length; j++) {
              flattened.push(cg[i][j]);
            }
          }
          return flattened;
        };

        scope.$watchCollection(flattenedClauseGroups, function() {
          var qm = scope.queryModel ? scope.queryModel: {};
          var vv = {
            outerConj:qm.outerConj,
            innerConj:qm.innerConj,
            clauseGroups:qm.clauseGroups,
            plainQuery:qm.plainQuery
          };
          // console.log('QB scope model changed; updating vv:', vv);
          ngModel.$setViewValue(vv);
        }, true );

        //$render: $viewValue -> our scope
        ngModel.$render = function() {
          // console.log('QB: rendering this viewValue to scope:',ngModel.$viewValue);
          var vv = ngModel.$viewValue;

          //WTF: Fix viewvalue - innerConj for some reason goes away when i added stateStorage to the dataTable
          if(vv.outerConj && !vv.innerConj)
            vv.innerConj = oppositeConj[vv.outerConj];
          if(vv.innerConj && !vv.outerConj)
            vv.outerConj = oppositeConj[vv.innerConj];

          scope.queryModel = ngModel.$viewValue;
        };

      },

      controller: function($scope) {

        DbuiFieldType.getAugmentedFieldList($scope.boClass, true, true).then(function(fieldList) {
            $scope.fieldList = fieldList;
        });

        var typeDescMap = db[$scope.boClass]._bo_meta_data.type_desc_map;

        var oppositeConj = {$and:'$or', $or:'$and'};
        $scope.conjLabels = {$and:'and', $or:'or'};

        //Acertain some info to create a sensible "default" query when new terms are added
        var allFields = Object.keys(typeDescMap);
        allFields.sort();
        for(var i=0; i < allFields.length; i++) {
          var td = typeDescMap[allFields[i]];
          var opList = td && DbuiFieldType.getOpList(td);

          if(td && allFields[i].indexOf('$') !== 0 &&
            allFields[i].indexOf('_') !== 0 && opList) {

            $scope.stubField = allFields[i];
            $scope.stubQueryClause = {};
            $scope.stubQueryClause[opList[0].op]='';
            break;
          }
        }

        var newStubQuery = function() {
          //Copy from clausegroup[0][0]
          var copyFrom = $scope.queryModel.clauseGroups[0][0];
          var stub = {};
          for(var k in copyFrom) {
            if(k.indexOf('$$') === -1)
              stub[k] = copyFrom[k];
          }
          // console.log('copy from:', copyFrom);
          // console.log('new stub query:', stub);
          return stub;
        };

        //Add a new term to a clauseGroup
        $scope.newTerm = function(clauseGroup, conj) {
          var qm = $scope.queryModel;
          if(conj) {
            qm.innerConj = conj;
            qm.outerConj = oppositeConj[conj];
          }
          clauseGroup.push(newStubQuery());
        };

        //Add a new clauseGroup to the query
        $scope.newClauseGroup = function() {
          var qm = $scope.queryModel;
          qm.clauseGroups.push([newStubQuery()]);
        };

        $scope.removeTerm = function(clauseGroup, index) {
          var cg = $scope.queryModel.clauseGroups;

          if(clauseGroup.length === 1) {
            for(var i=0; i < cg.length; i++) {
              if(cg[i] === clauseGroup) {
                cg.splice(i, 1);
                break;
              }
            }
          }
          else {
            clauseGroup.splice(index, 1);
          }

          if(cg.length === 1 && cg[0].length === 1)
            $scope.queryModel.outerConj = $scope.queryModel.innerConj = null;

          if(cg.length === 0) {
            $scope.onClose();
          }
        };


      }
    };
  }

function ($rootScope, $http, $q, db, $state, $cookies, AuthInterceptor) {

    var currentUser = null;
    var authToken = null;
    var initPromise = false;

    return {
      init: function() {
        if(initPromise)
          return initPromise;

        console.log('initializing Auth');
        var authToken = $cookies.get('access_token');

        if(authToken) {
          //we have an auth token -
          // send it to auth/login webservice to retreive user data

          var deferred = $q.defer();
          initPromise = deferred.promise;

          $http.post('auth/login', {}).then(
            function(resp) {
              var responseObj = resp.data;
              if(responseObj.error) {
                deferred.reject(responseObj.error);
              }
              else {
                authToken = responseObj.token;
                AuthInterceptor.setAuthToken(authToken);
                currentUser = responseObj.user;
                deferred.resolve(true);
                $rootScope.$emit('login', currentUser);
              }
            },
            function(err) {
              var responseObj = err.data;
              if(responseObj) {
                err = responseObj.error;
              }
              deferred.reject(err);
            }
          );

        }
        else {
          //No auth token...
          initPromise = $q.resolve(true);
        }

        return initPromise;
      },

      login: function(username, password, remember) {
        var deferred = $q.defer();
        var paramObj = {username:username, password:password};
        $http.post('auth/login', paramObj).then(
          function(resp) {
            var responseObj = resp.data;
            if(responseObj.error) {
              deferred.reject(responseObj.error);
            }
            else {
              authToken = responseObj.token;
              AuthInterceptor.setAuthToken(authToken);
              currentUser = responseObj.user;
              deferred.resolve(true);
              $rootScope.$emit('login', currentUser);
            }
          },
          function(err) {
            var responseObj = err.data;
            if(responseObj) {
              err = responseObj.error;
            }
            deferred.reject(err);
          }
        );

        return deferred.promise;
      },

      logout: function() {
        currentUser = null;
        authToken = null;
        AuthInterceptor.setAuthToken(authToken);
        $rootScope.$emit('logout');
      },

      newUser: function() {

      },

      changePassword: function(newPassword) {
        var deferred = $q.defer();
        var paramObj = {password:newPassword};
        $http.post('auth/changePassword', paramObj).then(
          function(resp) {
            var responseObj = resp.data;
            if(responseObj.error) {
              deferred.reject(responseObj.error);
            }
            else {
              deferred.resolve(responseObj.result);
            }
          },
          function(err) {
            var responseObj = err.data;
            if(responseObj) {
              err = responseObj.error;
            }
            deferred.reject(err);
          }
        );

        return deferred.promise;
      },

      isLoggedIn: function() {
        return currentUser !== null;
      },

      getCurrentUser: function() {
        return currentUser;
      },

      onLogin: function(fn) {
        $rootScope.$on('login',fn);
        if(currentUser !== null) {
          fn(null, currentUser);
        }
      },

      onLogout: function(fn) {
        $rootScope.$on('logout',fn);
      }



    };

}

function () {
    return function (tel) {
      if (!tel) { return ''; }

      var value = tel.toString().replace(/^\+/, '');

      if (value.match(/[^0-9]/)) {
        return tel.replace(/\D/g,''); // removes anything not a number
      }

      var country, city, number;

      switch (value.length) {
        case 10: // +1PPP####### -> C (PPP) ###-####
          country = 1;
          city = value.slice(0, 3);
          number = value.slice(3);
          break;

        case 11: // +CPPP####### -> CCC (PP) ###-####
          country = value[0];
          city = value.slice(1, 4);
          number = value.slice(4);
          break;

        case 12: // +CCCPP####### -> CCC (PP) ###-####
          country = value.slice(0, 3);
          city = value.slice(3, 5);
          number = value.slice(5);
          break;

        default:
          return tel;
      }

      if (country == 1) {
          country = '';
      }

      number = number.slice(0, 3) + '-' + number.slice(3);

      return (country + ' (' + city + ') ' + number);
    };
  }

function ($compile, $injector, DbuiFieldType) {
    return {
        // template:'<div ng-include src="fieldTemplateUrl"></div>',
        template:'<div></div>',
        restrict: 'E',

        require:'ngModel',

        scope: {
            typeDesc: '<',
            elemId: '<',
            fieldCustomizations: '<?', //From perspective.field_customizations
            contextObject: '<?', 
            linkStatus: '=?'
        },

        link: function(scope, iElement, iAttributes, ngModel) {

            var td = scope.typeDesc;
            if(!td) {
                console.error('Missing typeDesc in dbuiFieldEditor directive');
                return;
            }


            DbuiFieldType.getSpec(td, 'edit', scope.fieldCustomizations).then(function(specObj){

                if(!specObj) {
                    console.error('Missing UI Spec in dbuiFieldEditor directive');
                    return;
                }

                //First, load up the proper type-specific template from DbuiFieldType service, 
                // get it compiled and attached to this directive:
                var childTemplate = specObj.template; 
                iElement.html($compile(childTemplate)(scope));

                //Next, handle custom linkage logic for type-specific UI Spec
                var performNgModelSetup = true; //Type-specific link can override our default ng-model binding.


                if(specObj.link_fn) {

                  //This fieldtype has a 'link' function defined: 
                  //  returns true if we should go ahead and set up default ngModel binding  
                  //     (lets the child simply update scope.binding.value)
                  //  returns false if it already took care of it 
                  //     (it set up some fancy custom ng-model setup for its own purposes)

                  var fnString = specObj.link_fn;
                  try {
                      var toCall;
                      eval("toCall = "+fnString);
                      if(typeof toCall === 'function') {
                          performNgModelSetup = $injector.invoke(toCall, this, 
                            {scope:scope, iElement:iElement, iAttributes:iAttributes, ngModel:ngModel}
                            );
                      }
                      else {
                          console.error('bad link function for typeDesc', td);
                      }
                  }
                  catch(err) {
                      console.error('bad link function for typeDesc', td, err);
                  }

                }


                if(performNgModelSetup) {
                    //Perform the 'default' ngModel setup: child template/controller can just assign to scope object binding.value
                    // "modelValue" is the object that is ultimately assigned to whatever our parent put in the ng-model directive
                    // "$viewValue" is the representation we manipulate internally; in this default case it's just a wrapper around modelValue

                    //Four parts to setting it up:

                    //1. Wire up converter for ng-model object --> internal $viewValue representation
                    ngModel.$formatters.push(function(modelValue) {
                        //  console.log('dbuiFieldEditor: formatting viewValue',modelValue);
                        return {value:modelValue};
                    });

                    //2. Wire up converter for internal $viewValue representation --> ng-model object
                    ngModel.$parsers.push(function(viewValue) {
                        //  console.log('dbuiFieldEditor: parsing viewValue', viewValue);
                        return viewValue.value;
                    });

                    //3. Wire up trigger for scope object --> $viewValue
                    scope.$watch('binding', function() {
                        //  console.log('dbuiFieldEditor: detected change in scope.binding:', scope.binding);  
                        //must *replace* the viewValue object in order for change to propogate to ng-model!
                        if(scope.binding && scope.binding.value !== ngModel.$viewValue.value) {
                            ngModel.$setViewValue({value:scope.binding.value});
                        }
                    }, 
                    true); //deep watch... TODO performace issue to deep watch all field values???

                    //4. Wire up callback for $viewValue update --> scope object
                    ngModel.$render = function() {
                        //  console.log('dbuiFieldEditor: rendering viewValue to scope.binding:', ngModel.$viewValue);

                        if(!scope.binding) {
                            scope.binding = {};
                        }
                        //scope object can refer directly to the ngModel viewValue (just not the other way around!)
                        scope.binding.value = ngModel.$viewValue.value;
                    };

                }

                /* ngModel setup is now complete.  However, it was done asyncronously (having waited for 
                 * the DbuiFieldType.getSpec() promise) so its $viewValue may already contain data that has
                 * yet to be rendered...
                 */
                 if(typeof ngModel.$render === 'function') {
                     //Simulate the standard ngModel sequence
                     var initValue = ngModel.$viewValue;
                     _.forEach(ngModel.$formatters, function(formatter) {
                        if(typeof formatter === 'function') {
                            initValue = formatter(initValue);
                        }
                     });
                     ngModel.$setViewValue(initValue);
                     ngModel.$render();
                     ngModel.$setPristine();
                 }

                 scope.linkStatus = true;
            });

        },


      controller: function($scope) {

          DbuiFieldType.getSpec($scope.typeDesc, 'edit', $scope.fieldCustomizations).then(function(specObj){

              if(specObj && specObj.controller_fn) {
                  var fnString = specObj.controller_fn;
                  try {
                      var toCall;
                      eval("toCall = "+fnString);
                      if(typeof toCall === 'function') {
                          return $injector.invoke(toCall, this, {$scope:$scope});
                      }
                      else {
                          console.error('bad controller function for typeDesc', $scope.typeDesc);
                      }
                  }
                  catch(err) {
                      console.error('bad controller function for typeDesc', $scope.typeDesc, err);
                  }
              }
          });


      }

    };
  }

function () {
    var db;

    /**
     * Constructor for type descriptor maps: special dynamic versions of BO definitions
     * @constructor
     * @param {!Object.<string, Object>} fieldToTd A plain object version of the typeDesc map
     **/
    var TypeDescMap = function(fieldToTd) {
        if(!this || !(this instanceof TypeDescMap)) {
            return new TypeDescMap(fieldToTd);
        }

        _.assign(this, fieldToTd);

        //Recursively Object-ify type_desc_map's stashed in any composite td's
        for(var fieldName in fieldToTd) {
            if(this[fieldName].type_desc_map) {
                this[fieldName].type_desc_map = new TypeDescMap(this[fieldName].type_desc_map);
            }
            else if(this[fieldName] instanceof Array && this[fieldName].length > 0 && this[fieldName][0].type_desc_map) {
                this[fieldName][0].type_desc_map = new TypeDescMap(this[fieldName][0].type_desc_map);
            }
        }
    };

    /**
     * @private 
     * getTypeDescriptor may need access to db when traversing into reference fields.  
     * However, simply injecting db as a factory causes a circular depenency.
     */
    TypeDescMap._init = function(dbService) {
        db = dbService;
    };

    /**
     * @function TypeDescMap#getTypeDescriptor
     * Retrieves a type descriptor for a particular field/sub-field; recursing into composites and references if needed.
     * @param {string} path can be a simple fieldname or dotted into reference or composite fields, e.g.:
     *     db.SomeBusinessObj._bo_meta_data.getTypeDescriptor('refField.blah');
     **/
    Object.defineProperty(TypeDescMap.prototype, 'getTypeDescriptor', {
        enumerable:false, writable:false,
        value:function(path) {
            var dotPos = path.indexOf('.');

            if(dotPos === -1) { //no dot -> just a field name
                return this[path];
            }

            var localField = path.substring(0, dotPos);
            var subPath = path.substring(dotPos+1);

            var localTd = this[localField];

            if(!localTd) {
              console.error('invalid fieldname for td', localField, this);
              return null;
            }

            if(localTd instanceof Array && localTd.length > 0) {
                localTd = localTd[0];
            } 

            if(localTd.type === 'reference') {
                var RefModel = db[localTd.ref_class];
                if(!RefModel) {
                    console.error('invalid reference class in type descriptor:', localTd);
                    return null;
                }

                return RefModel._bo_meta_data.type_desc_map.getTypeDescriptor(subPath);
            }
            else if(localTd.type === 'composite') {
                var subTd = localTd.type_desc_map;
                if(!subTd) {
                    console.error('composite type descriptor missing child type_desc_map', localTd);
                }

                return subTd.getTypeDescriptor(subPath);
            }
            else {
              //dotted into a non-reference or a non-existent field:
              console.log('invalid field specifier for this type descriptor', this, subPath);
              return null;
            }

          } //end function
      });



    return TypeDescMap;
}

function ($uibModal, $rootScope) {

    var perspectiveEditorController = function ($scope, $timeout, $q, db,  NoonI18n,  DbuiFieldType) {
        var className = $scope.className;
        var perspectiveObj = $scope.perspectiveObj;
        var labels = NoonI18n.getBoLabelGroup(className);

        var fieldList = $scope.fieldList = perspectiveObj.fields;

        var availableFields;
        var availableFieldMap = {};
        var indexAvailableFields = function() {
            for(var i=0; i< availableFields.length; i++) {
                availableFieldMap[availableFields[i].fieldName] = availableFields[i];
            }
        };


        DbuiFieldType.getAugmentedFieldList(className, true).then(function(fieldInfoList) {
            availableFields = $scope.availableFields = fieldInfoList;
            indexAvailableFields();

            for(var i=0; i< fieldList.length; i++) {
                var fi = availableFieldMap[fieldList[i]];
                if(fi) {
                    fi.included = true;
                }
                else if(fieldList[i].indexOf('.') > -1) {
                    //need to expand a ref field...
                    var dotPos = fieldList[i].indexOf('.');
                    var fieldName = fieldList[i].substring(0, dotPos+1);
                    fi = availableFieldMap[fieldName];

                    if(fi) {
                        fi.expand();
                        indexAvailableFields();
                        i--;
                        continue;
                    }
                }
            }
        });

        $scope.selectedIndex = -1;

        $scope.addField = function(fieldInfo) {
            if(!fieldInfo.refPlaceholder) {
                fieldInfo.included = true;
                fieldList.push(fieldInfo.fieldName);
                $scope.selectedIndex = fieldList.length - 1;
            }
            else {
                fieldInfo.expand();
                indexAvailableFields();
            }
        };

        $scope.moveUp = function() {
            var idx = $scope.selectedIndex;
            var obj = fieldList[idx];
            fieldList.splice(idx, 1); //Remove from it's current position
            fieldList.splice(idx-1, 0, obj); //insert it one higher

            $scope.selectedIndex--;
        };
        $scope.moveDown = function() {
            var idx = $scope.selectedIndex;
            var obj = fieldList[idx];
            fieldList.splice(idx, 1); //Remove from it's current position
            fieldList.splice(idx+1, 0, obj); //insert it one lower

            $scope.selectedIndex++;
        };

        $scope.remove = function(index) {
            var fieldName = fieldList[index];
            fieldList.splice(index, 1);
            availableFieldMap[fieldName].included = false;
            if(index === $scope.selectedIndex) {
                $scope.selectedIndex = -1;
            }
        };

        $scope.selectedForMove = function(fieldIndex) {
            return fieldIndex === $scope.selectedIndex;
        };

        $scope.selectForMove = function(fieldIndex) {
            $scope.selectedIndex = fieldIndex;
        };

        $scope.canMoveUp = function() {
            return $scope.selectedIndex > 0;
        };

        $scope.canMoveDown = function() {
            return $scope.selectedIndex >= 0 && $scope.selectedIndex < fieldList.length -1;
        };

        $scope.getFieldLabel = function(fieldName) {
            return (labels._abbreviated  && labels._abbreviated[fieldName]) || labels[fieldName] || fieldName;
        };
    };


    /**
     * DbuiPerspectiveEditor.showEditorDialog()
     * 
     * @param {string} className - name of the business object class to whom the perspective applies
     * @param {Perspective} perspectiveObj - object whose fields are being edited
     * @param {string} perspectiveType - type (list, edit, view, ...)
     * @return the uibModal "result" promise, resolved when the dialog is closed.
     * 
     */
    this.showEditorDialog = function(className, perspectiveObj, perspectiveType) {

        var modalInstance;
        var scope = $rootScope.$new(true);

        scope.title = 'Edit Perspective for '+className;
        scope.className = className;
        scope.perspectiveObj = perspectiveObj;
        scope.perspectiveType = perspectiveType;

        return $uibModal.open({
            templateUrl:'dbui/reusable/dialog/perspective_editor.html',
            size:'lg',
            scope: scope,
            controller:perspectiveEditorController
        }).result;

    };

}

