function (db, auth, Q, _) {
    var exports = {};
    var ENGLISH_ID='-9vPfv2lEeSFtiimx_V4dw';

    /**
     * DbuiMenuBuilder.buildMenu
     * takes a key, queryies for the menu, and dereferences any submenus
     * @return array of objects
     */ 
    var buildMenu = 
    exports.buildMenu = function(key, user) {

      //Helper function - called when a submenu is built;
      var dereferenceMenu = function(menuObj) {

        //this -> bound to the menu item w/ the "ref"
        if(menuObj && menuObj.length > 0)
          this.submenu = menuObj;
      }

      //Helper function - populates labels for a ref menuItem (bound to 'this')
      var populateLabels = function() {
        var menuItem = this;
        var lang = user && user.language ? user.language._id : ENGLISH_ID; 

        return db.LabelGroup.findOne({key:this.ref, 'language._id':lang}).then(
          function(labelGroupObj) {
            if(labelGroupObj) {
              var lg = labelGroupObj.value;

              //First, check this menu item
              if(lg._submenu_label)
                menuItem.label = lg._submenu_label;
              else
                menuItem.label = menuItem.ref;

              //Then, submenu labels...
              if(menuItem.submenu) {
                for(var i=0; i < menuItem.submenu.length; i++) {
                  var itemLabel = menuItem.submenu[i].label;
                  if(itemLabel && lg[itemLabel])
                    menuItem.submenu[i].label = lg[itemLabel];
                }
              }
            }
          });
      }

      var cleanupMenu = function(menuItemList) {

        for(var i=0; i < menuItemList.length; i++) {
          var m = menuItemList[i];
          if(!m.action && !m.submenu) {
            menuItemList.splice(i, 1);
            i--;
          }
        }
        return menuItemList;
      }

      return db.Menu.find({key:key}).then(
        function(results) {
          var menuToReturn = [];
          var promiseList = [];

          //Iterate through the Menu objects that matched our key...
          for(var i=0; i < results.length; i++) {
            var menuObj = results[i];

            //Check the roles  TODO possibly use $satisfies query instead...
            if(!menuObj.rolespec || auth.checkRolesForUser(user, menuObj.rolespec)) {
              var menuDef = menuObj.definition || [];
              if(!Array.isArray(menuDef))
                menuDef = [menuDef];

              //Pull in the menu items
              for(var j=0; j<menuDef.length; j++) {
                var menuItem = menuDef[j];
                menuToReturn.push(menuItem);

                if(menuItem.ref) {
                  //Dereference, and populate labels
                  promiseList.push(
                    buildMenu(menuItem.ref, user)
                      .then(dereferenceMenu.bind(menuItem))
                      .then(populateLabels.bind(menuItem))
                  );
                }
              }


            }
          }

          if(promiseList.length > 0)
            return Q.all(promiseList).then(function() { return cleanupMenu(menuToReturn)});
          else
            return cleanupMenu(menuToReturn);

        });

    };

    return exports;
}

function (db, auth, config, Q, _) {
    var exports = {};

    //Manual 'hoisting' of functions
    var autogenPerspective;
    var getPerspective;
    var getDisplayOptions;



    //Map perspective type to a function that generates a default perspective of that type:
    var perspectiveGenerators = {  

        list:function(targetTypeDescMap, perspectiveConfigItem) {
            perspectiveConfigItem.list.fields = [];
            for(var f in targetTypeDescMap) {
                if(f.indexOf('_') === 0) continue;
                perspectiveConfigItem.list.fields.push(f);
            }
        },

        picker_list:function(targetTypeDescMap, perspectiveConfigItem) {
            if(perspectiveConfigItem.list) {
                perspectiveConfigItem.picker_list = perspectiveConfigItem.list;
            }
            else {
                perspectiveConfigItem.picker_list.fields = [];
                for(var f in targetTypeDescMap) {
                    if(f.indexOf('_') === 0) continue;
                    perspectiveConfigItem.picker_list.fields.push(f);
                }
            }
        },

        view:function(targetTypeDescMap, perspectiveConfigItem) {
            perspectiveConfigItem.view.layout = [];
              for(var f in targetTypeDescMap) {
                  if(f.indexOf('_') === 0) continue;
                  perspectiveConfigItem.view.layout.push(f);
              }
        },

        'dialog-view':function(targetTypeDescMap, perspectiveConfigItem) {
            if(perspectiveConfigItem.view) {
                perspectiveConfigItem['dialog-view'] = perspectiveConfigItem.view;
            }
            else {
              perspectiveConfigItem['dialog-view'].layout = [];

              for(var f in targetTypeDescMap) {
                  if(f.indexOf('_') === 0) continue;
                  perspectiveConfigItem['dialog-view'].layout.push(f);
              }
            }
        },

        edit: function(targetTypeDescMap, perspectiveConfigItem) {
            perspectiveConfigItem.edit.layout = [];
              for(var f in targetTypeDescMap) {
                  if(f.indexOf('_') === 0) continue;
                  perspectiveConfigItem.edit.layout.push(f);
              }
        }
    };

    /**
     * @private
     */
    var getTypeDescMap = function(boClass) {

        if(db[boClass]) {
            return db[boClass]._bo_meta_data.type_desc_map;
        }
        else if(boClass.indexOf('#') > 0) {
            var hashPos = boClass.indexOf('#');
            var baseClass = boClass.substring(0, hashPos);
            var subField = boClass.substring(hashPos+1, boClass.length).replace(/#/g, '.');

            var fieldTd = db[baseClass]._bo_meta_data.getTypeDescriptor(subField);
            if(fieldTd) {
                if(fieldTd instanceof Array) {
                    fieldTd = fieldTd[0];
                }
                return fieldTd.type_desc_map;
            }
        }
        else {
            throw new Error('Invalid class name '+boClass);
        }

    };

    /**
     * DbuiPerspectiveBuilder.autogenPerspective
     */
    autogenPerspective = 
    exports.autogenPerspective = function(boClass, perspectiveName, perspectiveType, perspectiveConfigItem) {
      console.log("Auto-generating perspective: "+perspectiveName+"."+boClass+"."+perspectiveType);

      var td = getTypeDescMap(boClass); //db[boClass]._bo_meta_data.type_descriptor;
      var key = 'sys.dbui.perspective.'+perspectiveName+'.'+boClass;


      if(!perspectiveConfigItem)
        perspectiveConfigItem = {};

      var deferred = Q.defer();

      //If we're not generating the default perspective, see if there's one to copy...
      if(perspectiveName !== "default") {
        var defaultKey = 'sys.dbui.perspective.default.'+boClass;

        config.getParameter(defaultKey, false)
          .then( function(defaultConfigItem) {
            if(defaultConfigItem && defaultConfigItem[perspectiveType]) {
              //We found the default... copy from it in below then().
              return defaultConfigItem;
            }
            else {
              //No default for this perspective/type; auto-generate and copy from it..
              return autogenPerspective(boClass, 'default', perspectiveType, defaultConfigItem);
            }

          })
          .then(function(perspectiveToCopyFrom) {
            perspectiveConfigItem[perspectiveType] = perspectiveToCopyFrom[perspectiveType];
            deferred.resolve(perspectiveConfigItem);
            //Don't save the non-default perspective
            // config.saveParameter(key, perspectiveConfigItem).then(
            //   function() {deferred.resolve(perspectiveConfigItem)}
            // );
          });
      }
      else { //Generating a default perspective

        if(!perspectiveGenerators[perspectiveType]) {
            deferred.reject("bad perspectiveType: "+perspectiveType);
        }
        else {
            perspectiveConfigItem[perspectiveType] = {};

            perspectiveGenerators[perspectiveType](td, perspectiveConfigItem);

            config.saveParameter(key, perspectiveConfigItem).then(
                function() {deferred.resolve(perspectiveConfigItem);},
                function(err) {deferred.reject(err)}
            );
        }

      }

      return deferred.promise;
    }

    /**
     * DbuiPerspectiveBuilder.getDisplayOptions
     * @private
     *  Merges sys.dbui.displayoptions w/ sys.dbui.displayoptions.SpeecificClass
     *  @return an object: {field_name:{customizations}}
     **/
    getDisplayOptions = function(boClass, userId, perspectiveName, perspectiveType) {
      var rootKey = 'sys.dbui.displayoptions';
      var classKey = rootKey+'.'+boClass;


      var promiseArray = [
          config.getCustomizedParameter(rootKey, userId),
          config.getCustomizedParameter(classKey, userId)
      ];

      //Also, incorporate composite field 'sub-perspectives' 
      var typeDescMap = getTypeDescMap(boClass);//db[boClass]._bo_meta_data.type_descriptor;
    //   var composites = [];

    //   _.forEach(typeDescMap, function(td, fieldName) {
    //       if(td.type === 'composite') {
    //           composites.push(fieldName);
    //           promiseArray.push(getPerspective(boClass+'#'+fieldName, perspectiveName, perspectiveType, userId));
    //       }
    //       //TODO check for named composites???
    //   });

      return Q.all(promiseArray).then(function(resultArr) {
        var rootObj = resultArr[0] || {};
        var classObj = resultArr[1] || {};
        var result = {};

        //the base sys.dbui.displayoptions contains params for specific field types.
        // the class-specific one contains params based on field name...

        //Iteratate through the type descriptor's to map params to appropriate field names:
        _.forEach(typeDescMap, function(td, fieldName) {
          if(classObj[fieldName]) {
            result[fieldName] = rootObj[td.type] ? _.clone(rootObj[td.type]) : {};
            _.merge(result[fieldName], classObj[fieldName]);
          }
          else if(rootObj[td.type]) {
            result[fieldName] = _.clone(rootObj[td.type]);
          }
        });

        //Merge any composite 'sub-perspectives' into result
        // var arrIndex = 2; //remainder of resultArr are result of getPerspective() calls
        // _.forEach(composites, function(fieldName) {
        //     if(!result[fieldName]) {
        //         result[fieldName] = {};
        //     }
        //     if(!result[fieldName].perspective) {
        //         result[fieldName].perspective = {};
        //     }
        //     result[fieldName].perspective[perspectiveType] = resultArr[arrIndex];
        //     arrIndex++;
        // });

        return result;
      });
    }

    /**
     * DbuiPerspectiveBuilder.getPerspective
     */
    getPerspective = 
    exports.getPerspective = function(boClass, perspectiveName, perspectiveType, userId) {

      var rootKey = 'sys.dbui.perspective';         //any perspective, any BO
      var baseKey = rootKey+'.'+perspectiveName;    //specific perspective, any BO
      var classKey = baseKey+'.'+boClass;           //specific perspective, specific BO
      var wildcardKey = rootKey+'.*.'+boClass;      //any perspective, specific BO

      //Skip merging of rootKey and baseKey perspectives for composite perspectives
      var skipMerge = boClass.indexOf('#') > -1; //e.g. BoClass#compositeField

      return config.getCustomizedParameter(classKey, userId).then(function(result) {

        var promiseArray;

        if(!skipMerge) {
            promiseArray = [
              config.getCustomizedParameter(rootKey, userId),
              config.getCustomizedParameter(baseKey, userId),
              config.getCustomizedParameter(wildcardKey, userId)
            ];
        }
        else {
            promiseArray = [Q(false), Q(false), Q(false)];
        }

        if(result && result[perspectiveType]) {
          promiseArray.push(Q(result));
        }
        else {
          promiseArray.push(
            autogenPerspective(boClass, perspectiveName, perspectiveType, result)
          );
        }

        //Finally add in displayoptions:
        promiseArray.push(getDisplayOptions(boClass, userId, perspectiveName, perspectiveType));

        return Q.all(promiseArray);
      })
      .then(function(configArray) {

        //so _.merge() behaves properly w/ array values:
        var arrayMerger = function(objectVal, sourceVal) {
          if(_.isArray(sourceVal))
            return sourceVal;
          else
            return undefined; //default merger
        }

        var root = configArray[0] || {};
        var base = configArray[1] || {};
        var wild = configArray[2] || {};
        var clazz = configArray[3];
        var displayOptions = configArray[4];

        // console.log('ROOT %j BASE %j CLAZZ %j', root, base, clazz);

        var merged = _.merge(root, base, arrayMerger); //base onto root...
        _.merge(merged, wild, arrayMerger);  //wild onto merged base/root
        _.merge(merged, clazz, arrayMerger); //clazz atop the whole thing

        var perspectiveObj = merged[perspectiveType];

        if(!perspectiveObj.fieldCustomizations) {
          perspectiveObj.fieldCustomizations = {};
        }

        //merge fieldCustomizations atop more general displayOptions
        _.merge(displayOptions, perspectiveObj.fieldCustomizations);
        perspectiveObj.fieldCustomizations = displayOptions;

        return perspectiveObj;


      });


    };


    return exports;
}

function (db, _, Q) {
    var exports = {};
    var ENGLISH_ID='-9vPfv2lEeSFtiimx_V4dw';

    /**
     * @private
     */
    var autogenLabelGroup = function(className) {
      var key = 'sys.dbui.bo.'+className;

      var typeDescMap;

      if(db[className]) {
          typeDescMap = db[className]._bo_meta_data.type_descriptor;
      }
      else if(className.indexOf('#') > -1) {
          var hashPos = className.indexOf('#');
          var baseClass = className.substring(0, hashPos);
          var field = className.substring(hashPos+1);

          var td = db[baseClass] && db[baseClass]._bo_meta_data.type_descriptor[field];

          if(td instanceof Array) {
              td = td[0]
          }

          if(td && td.type === 'composite') {
              typeDescMap = td.type_desc_map;
          }
      }

      if(typeDescMap) {
        console.log("Auto-generating LabelGroup for %s", className);
         //Create a LabelGroup for i18n
         var lg = new db.LabelGroup({
           key:key,
           language:{_id:'-9vPfv2lEeSFtiimx_V4dw'},
           value:{}
         });

         for(var f in typeDescMap) {
           if(f.indexOf('_') !== 0) {
             lg.value[f] = _.startCase(f);
           }
         }

         return lg.save();
       }
       return null;
    };

    /**
     * I18n.getLabelGroup
     * Retrieve label group for a particular user.
     */
    exports.getLabelGroup = function(key, user) {
      var lang = user && user.language ? user.language._id : ENGLISH_ID; //default to english

      return db.LabelGroup.findOne({key:key, 'language._id':lang}).then(function(lg){
        return lg && lg.value;
      });

    };

    /**
     * I18n.aggregateLabelGroups
     * Retrieve multiple label groups for a particular user.
     */
    exports.aggregateLabelGroups = function(keyArr, user) {
      var lang = user && user.language ? user.language._id : ENGLISH_ID; //default to english

      return db.LabelGroup.find({key:{$in:keyArr}, 'language._id':lang}).then(function(lgArr){
          var result = {};
          _.forEach(lgArr, function(lg) {
              result[lg.key] = lg.value;
          });
        return result;
      });

    };

    /**
     * I18n.getBoLabelGroup
     * *resolves immediate reference sub-fields; TODO also resolve inheritance!
    */
    exports.getBoLabelGroup = function(className, user) {
      var lang = user && user.language ? user.language._id : ENGLISH_ID; //default to english


      var keyPrefix = 'sys.dbui.bo.';
      var baseKey = keyPrefix+className;

      var boMetaData;

      if(db[className]) {
          boMetaData = db[className]._bo_meta_data;
      }
      else if(className.indexOf('#') > -1) {
          var hashPos = className.indexOf('#');
          var baseClass = className.substring(0, hashPos);
          var field = className.substring(hashPos+1);

          var td = db[baseClass] && db[baseClass]._bo_meta_data.type_descriptor[field];

          if(td instanceof Array) {
              td = td[0]
          }

          if(!td || td.type !== 'composite') {
              throw new Error('Invalid class name: '+className);
          }

          boMetaData = {
              type_descriptor:td.type_desc_map
          };
      }
      else {
          throw new Error('Invalid class name: '+className);
      }

      var queryKeyList = [baseKey]; //LabelGroup keys we'll be asking for

      var fieldToLgKey = {}; //Maps a ref field name to correpsonding LabelGroup key

      //first, determine all the reference fields and build a list of corresponding LabelGroup keys.
      _.forEach(boMetaData.type_descriptor, function(td, fieldName) {
        if(td.type === 'reference') {
          var myKey = keyPrefix+td.ref_class;
          queryKeyList.push(myKey);
          fieldToLgKey[fieldName] = myKey;
        }
      });

      //Query for all the necessary LabelGroup's
      return db.LabelGroup.find({key:{$in:queryKeyList}, 'language._id':lang}).then(function(lgList){
        var lgMap = _.indexBy(lgList, 'key');
        var composite;
        var initPromise;

        if(lgMap[baseKey]) {
          composite = lgMap[baseKey].value;
          initPromise = Q(true);
        }
        else {
          initPromise = autogenLabelGroup(className).then(function(newLg) {
            composite = newLg.value;
          });
        }

        return initPromise.then(function() {
          _.forEach(fieldToLgKey, function(lgKey, fieldName) {
            if(lgMap[lgKey]) {
              var fieldPrefix = fieldName+'.';
              var labelPrefix = ''; //(composite[fieldName] || fieldName)+': ';

              _.forEach(lgMap[lgKey].value, function(subLabel, subField) {
                composite[fieldPrefix+subField] = labelPrefix+subLabel;
              });
            }
          });

          return composite;
        });
      });
    };


    return exports;
}

