      useWrapMode : true,
      showGutter: true,
      require: ['ace/ext/language_tools'],
      advanced: {
          enableSnippets: false,
          enableBasicAutocompletion: true,
          enableLiveAutocompletion: true
      }
    };

    $scope.aceInit.onLoad = function(editor) {
        $scope.aceEditor = editor;
        editor.$blockScrolling = Infinity;
