function (config, req, db, _) {
    var configKey = 'sys.dbui.navbar_menu';

    var fullBoList = {};


    var buildSubmenuContainer = function(categoryObj) {

        var submenuContainer = { label:categoryObj.title, submenu:[] };

        _.forEach(categoryObj.classes, function(boClass) {
            submenuContainer.submenu.push({
                action: {
                    state: "dbui.list",
                    params: { className:boClass, perspective:"default" }
                },
                label:boClass
            });
            delete fullBoList[boClass];
        });

        return submenuContainer;
    }

    return db.BusinessObjectDef.find({}).then(function(boList) {
        _.forEach(boList, function(bo) {
            fullBoList[bo.class_name] = bo._id;
        });

        return config.getCustomizedParameter(configKey, req.user._id);
    })
    .then(function(categoryArr) {
        //for now, menukey contains categorized list of BusinessObject names:
        /*
            [
                { title:"Angular Dev", classes:["AngularModule","AngularBlah",...] },
                { ... },
                ...
            ]
        */

        var menuArr = [];
        _.forEach(categoryArr, function(categoryObj) {
            menuArr.push(buildSubmenuContainer(categoryObj));
        });


        //Divvy the ones not covered in sys.dbui.navbarmenu according to package.
        if(Object.keys(fullBoList).length) {
            return db.BusinessObjectPackage.find({}, {name:1, 'manifest.BusinessObjectDef':1}).sort({name:1}).then(function(bops) {
                //map package to classes
                var pkgToClasses = {};

                _.forEach(bops, function(bopObj) {
                    if(bopObj.manifest && bopObj.manifest.BusinessObjectDef) {
                        var pkgBos = bopObj.manifest.BusinessObjectDef;
                        _.forEach(fullBoList, function(boId, boClass) {
                            if(pkgBos[boId]) {
                                pkgToClasses[bopObj.name] = pkgToClasses[bopObj.name] || [];
                                pkgToClasses[bopObj.name].push(boClass);

                            }
                        })
                    }
                });

                _.forEach(pkgToClasses, function(classArr, pkgName) {
                    classArr.sort();
                    menuArr.push(buildSubmenuContainer({title:pkgName, classes:classArr}));
                });

                var remaining = Object.keys(fullBoList);
                if(remaining.length) {
                    remaining.sort();
                    menuArr.push(buildSubmenuContainer({title:"(no package)", classes:remaining}));
                }

                return menuArr;
            });
        }
        else {
            return menuArr;
        }
    });

}

function (queryParams, res, db, _) {
    //Build response that maps type name to an 'info' object, providing what client-side needs in order to build a UI form for a specified business object.

    var fieldtypeName = queryParams.type;
    var isArray = queryParams.array === 'true';
    var viewEdit = queryParams.view_or_edit;

    console.log('getFieldTemplate for %s %s %s', viewEdit, fieldtypeName, (isArray ? '[ ]' : ''));

    if(!fieldtypeName) {
        throw new Error('Invalid field type name: '+fieldtypeName);
    }

    var applicKey = (viewEdit === 'view') ? 'for_viewing' : 'for_editing';

    if(isArray) {
        applicKey += '_array';
    }

    var queryObj = {
        'fieldtypes.name':fieldtypeName
    };
    queryObj[applicKey] = true;



    // console.log('queryObj %j', queryObj);
    return db.FieldTypeUiSpec.find(queryObj).then(function(uiSpecs) {

        if(uiSpecs.length > 1) {
            console.warn('Too many FieldTypeUiSpecs for %s %s %s', viewEdit, fieldtypeName, (isArray ? '[ ]' : ''))
        }
        if(uiSpecs.length === 0) {
            //look 'universally-applicable' editors/viewers:    
            delete queryObj['fieldtypes.name'];
            queryObj.fieldtypes = {$empty:true};

            return db.FieldTypeUiSpec.find(queryObj).then(function(universalSpecs) {
               if(!universalSpecs || !universalSpecs.length) {
                   throw new Error('No FieldTypeUiSpecs found for '+viewEdit+' '+fieldtypeName+' '+isArray);
               }
               res.type('html');
               return universalSpecs[0].template;
            });
        }

        res.type('html');
        return uiSpecs[0].template;

    });

}

function (db, auth, req, Q, _) {


    return auth.getCurrentUser(req).then(function(currUser) {
        var ENGLISH_ID='-9vPfv2lEeSFtiimx_V4dw';
        var language = (currUser.language && currUser.language._id) || ENGLISH_ID;

        return Q.all([
            db.BusinessObjectDef.find({}).lean().exec(),
            db.MemberFunction.find({applies_to:{$in:['client', 'both']}}).lean().exec(),
            db.LabelGroup.find({key:/^sys\.dbui\.bo\..+/, language:language}).lean().exec()
        ]);
    })
    .then(function(resultArr) {
        var allBods = resultArr[0];
        var memberFns = resultArr[1];
        var labelGroups = resultArr[2];

        var bodMap = _.indexBy(allBods, '_id');
        _.forEach(memberFns, function(memFn) {
            if(memFn.business_object && memFn.business_object._id) {
                var bod = bodMap[memFn.business_object._id];
                bod._member_functions = bod._member_functions || [];
                bod._member_functions.push(memFn);
                delete memFn.business_object;
                delete memFn.applies_to;
            }
        });
        bodMap = _.indexBy(allBods, 'class_name');
        _.forEach(labelGroups, function(lg) {
            var className = lg.key.split('.').pop();
            if(bodMap[className]) {
                bodMap[className]._field_labels =  lg.value;
            }
        });

        return {result:allBods};
    });
}

